import {
  Resolvers,
  Team as GraphQLTeam,
  User as GraphQLUser,
  CreateTeamInput,
  UpdateTeamInput,
  PaginationInput,
  QueryTeamArgs,
  QueryTeamsArgs,
  MutationCreateTeamArgs,
  MutationUpdateTeamArgs,
  MutationDeleteTeamArgs,
  MutationAddTeamMemberArgs,
  MutationRemoveTeamMemberArgs
} from '../../../../lib/generated/graphql';
import { GraphQLError } from 'graphql';
import { GraphQLContext } from '../index';

// Helper to get current user ID, throwing error if not authenticated
const getCurrentUserId = (context: GraphQLContext): string => {
  if (!context.currentUser?.id) {
    throw new GraphQLError('User is not authenticated', { extensions: { code: 'UNAUTHENTICATED' } });
  }
  return context.currentUser.id;
};

// Helper to map DB team row to GraphQLTeam (excluding relational fields handled by field resolvers)
// Exported for use in other resolvers like userResolvers.ts
export const mapDbTeamToGraphQLTeam = (dbTeam: unknown): (Partial<GraphQLTeam> & { _team_lead_user_id?: string, _created_by_user_id?: string }) | null => {
  if (!dbTeam || typeof dbTeam !== 'object') return null;
  // Type guard or assertion might be needed if accessing properties directly on unknown
  const team = dbTeam as Record<string, any>; // Assert to access properties

  return {
    id: team.id,
    name: team.name,
    description: team.description,
    createdAt: team.created_at, // Ensure DateTime scalar handles string conversion, map to camelCase
    updatedAt: team.updated_at, // Map to camelCase
    // Fields to be resolved by Team field resolvers:
    // teamLead (based on team.team_lead_user_id)
    // members (based on team.id)
    // createdBy (based on team.created_by_user_id)

    // Store these raw IDs on the object for field resolvers to use
    _team_lead_user_id: team.team_lead_user_id,
    _created_by_user_id: team.created_by_user_id,
  };
}

export const teamResolvers: Resolvers<GraphQLContext> = {
  Query: {
    team: async (_parent: unknown, args: QueryTeamArgs, context: GraphQLContext): Promise<GraphQLTeam | null> => {
      const { supabase } = context;
      const { id } = args;
      // RLS policies on 'teams' table should enforce visibility.
      const { data: dbTeam, error } = await supabase
        .from('teams')
        .select('*')
        .eq('id', id)
        .single();

      if (error) {
        console.error(`Error fetching team ${id}: ${error.message}`);
        // If RLS causes a 404-like error (e.g., 0 rows), it might not be a server error.
        // Supabase errors often have a code (e.g., 'PGRST116' for 0 rows with .single())
        if (error.code === 'PGRST116') return null; // Not found or not permitted
        throw new GraphQLError('Could not fetch team.', { extensions: { code: 'INTERNAL_SERVER_ERROR' } });
      }
      return mapDbTeamToGraphQLTeam(dbTeam) as GraphQLTeam;
    },
    teams: async (_parent: unknown, args: QueryTeamsArgs, context: GraphQLContext): Promise<GraphQLTeam[]> => {
      const { supabase } = context;
      let query = supabase.from('teams').select('*');

      if (args.pagination) {
        const limit = args.pagination.limit ?? 20;
        const offset = args.pagination.offset ?? 0;
        query = query.range(offset, offset + limit - 1);
      }
      // TODO: Add ordering from args if provided
      // TODO: Add ordering, e.g., .order('name', { ascending: true })

      const { data: dbTeams, error } = await query;

      if (error) {
        console.error(`Error fetching teams: ${error.message}`);
        throw new GraphQLError('Could not fetch teams.', { extensions: { code: 'INTERNAL_SERVER_ERROR' } });
      }
      return dbTeams ? dbTeams.map(dbTeam => mapDbTeamToGraphQLTeam(dbTeam) as GraphQLTeam) : [];
    },
  },
  Mutation: {
    createTeam: async (_parent: unknown, args: MutationCreateTeamArgs, context: GraphQLContext): Promise<GraphQLTeam | null> => {
      const currentUserId = getCurrentUserId(context);
      const { supabase } = context;
      const { input } = args;
      const { data: newDbTeam, error } = await supabase
        .from('teams')
        .insert({ name: input.name, description: input.description, team_lead_user_id: input.teamLeadUserId, created_by_user_id: currentUserId })
        .select().single();
      if (error) { console.error(`Error creating team: ${error.message}`); throw new GraphQLError('Could not create team.'); }
      return mapDbTeamToGraphQLTeam(newDbTeam) as GraphQLTeam;
    },
    updateTeam: async (_parent: unknown, args: MutationUpdateTeamArgs, context: GraphQLContext): Promise<GraphQLTeam | null> => {
      getCurrentUserId(context); 
      const { supabase } = context;
      const { id, name, description, teamLeadUserId } = args.input;
      const updatePayload: Partial<CreateTeamInput & { team_lead_user_id: string }> = {};

      // Handle InputMaybe<string> for name and description
      if (name !== undefined) updatePayload.name = name === null ? undefined : name;
      if (description !== undefined) updatePayload.description = description === null ? undefined : description;
      // teamLeadUserId is likely string | undefined already, or needs similar handling if it's InputMaybe
      if (teamLeadUserId !== undefined) updatePayload.team_lead_user_id = teamLeadUserId === null ? undefined : teamLeadUserId;

      if (Object.keys(updatePayload).length === 0) throw new GraphQLError('No update fields provided.');
      const { data: updatedDbTeam, error } = await supabase.from('teams').update(updatePayload).eq('id', id).select().single();
      if (error) { console.error(`Error updating team ${id}: ${error.message}`); if (error.code === 'PGRST116') throw new GraphQLError('Team not found.'); throw new GraphQLError('Could not update team.'); }
      return mapDbTeamToGraphQLTeam(updatedDbTeam) as GraphQLTeam;
    },
    deleteTeam: async (_parent: unknown, args: MutationDeleteTeamArgs, context: GraphQLContext): Promise<boolean> => {
      getCurrentUserId(context);
      const { supabase } = context;
      const { id } = args;
      const { error } = await supabase.from('teams').delete().eq('id', id);
      if (error) { console.error(`Error deleting team ${id}: ${error.message}`); throw new GraphQLError('Could not delete team.'); }
      return true; 
    },
    addTeamMember: async (_parent: unknown, args: MutationAddTeamMemberArgs, context: GraphQLContext): Promise<GraphQLTeam | null> => {
      getCurrentUserId(context);
      const { supabase } = context;
      const { teamId, userId } = args;
      const { error } = await supabase.from('team_members').insert({ team_id: teamId, user_id: userId });
      if (error) { 
        if (error.code === '23505') throw new GraphQLError('User is already a member.'); 
        if (error.code === '23503') throw new GraphQLError('Team or User not found.');
        console.error(`Error adding team member: ${error.message}`); 
        throw new GraphQLError('Could not add member.'); 
      }
      const { data: dbTeam, error: teamError } = await supabase.from('teams').select('*').eq('id', teamId).single();
      if (teamError) { console.error(`Error refetching team ${teamId} after adding member: ${teamError.message}`); throw new GraphQLError('Member added, but failed to refetch team.');}
      return mapDbTeamToGraphQLTeam(dbTeam) as GraphQLTeam;
    },
    removeTeamMember: async (_parent: unknown, args: MutationRemoveTeamMemberArgs, context: GraphQLContext): Promise<GraphQLTeam | null> => {
      getCurrentUserId(context);
      const { supabase } = context;
      const { teamId, userId } = args;
      const { error } = await supabase.from('team_members').delete().match({ team_id: teamId, user_id: userId });
      if (error) { console.error(`Error removing team member: ${error.message}`); throw new GraphQLError('Could not remove member.'); }
      const { data: dbTeam, error: teamError } = await supabase.from('teams').select('*').eq('id', teamId).single();
      if (teamError) throw new GraphQLError('Member removed, but failed to refetch team.');
      return mapDbTeamToGraphQLTeam(dbTeam) as GraphQLTeam;
    },
  },
  Team: {
    teamLead: async (parentTeam: Partial<GraphQLTeam> & { _team_lead_user_id?: string }, _args: Record<string, never>, context: GraphQLContext): Promise<GraphQLUser | null> => {
      const teamLeadUserId = parentTeam._team_lead_user_id;
      if (!teamLeadUserId) return null;
      const { supabase } = context;
      const { data: userData, error } = await supabase
        .from('user_profiles') // Assuming user profiles table
        .select('user_id, display_name, avatar_url, email')
        .eq('user_id', teamLeadUserId)
        .single();
      if (error || !userData) {
        console.error(`Error fetching team lead profile ${teamLeadUserId}: ${error?.message}`);
        return null;
      }
      return { id: userData.user_id, display_name: userData.display_name, avatar_url: userData.avatar_url, email: userData.email } as GraphQLUser;
    },
    members: async (parentTeam: Pick<GraphQLTeam, 'id'>, _args: Record<string, never>, context: GraphQLContext): Promise<GraphQLUser[]> => {
      const { supabase } = context;
      const { data: memberLinks, error: memberError } = await supabase
        .from('team_members')
        .select('user_id, role')
        .eq('team_id', parentTeam.id);

      if (memberError) {
        console.error(`Error fetching members for team ${parentTeam.id}: ${memberError.message}`);
        return [];
      }
      if (!memberLinks || memberLinks.length === 0) return [];

      const userIds = memberLinks.map(ml => ml.user_id);
      const { data: usersData, error: usersError } = await supabase
        .from('user_profiles')
        .select('user_id, display_name, avatar_url, email')
        .in('user_id', userIds);

      if (usersError) {
        console.error(`Error fetching member profiles for team ${parentTeam.id}: ${usersError.message}`);
        return [];
      }
      return usersData ? usersData.map(u => {
        return {
          id: u.user_id,
          email: u.email || `${u.user_id.substring(0,8)}@placeholder.email`,
          display_name: u.display_name,
          avatar_url: u.avatar_url,
        } as GraphQLUser; 
      }) : [];
    },
    createdBy: async (parentTeam: Partial<GraphQLTeam> & { _created_by_user_id?: string }, _args: Record<string, never>, context: GraphQLContext): Promise<GraphQLUser | null> => {
      const createdByUserId = parentTeam._created_by_user_id;
      if (!createdByUserId) return null;
      const { supabase } = context;
      const { data: userData, error } = await supabase
        .from('user_profiles')
        .select('user_id, display_name, avatar_url, email')
        .eq('user_id', createdByUserId)
        .single();
      if (error || !userData) {
        console.error(`Error fetching creator profile ${createdByUserId}: ${error?.message}`);
        return null;
      }
      return { id: userData.user_id, display_name: userData.display_name, avatar_url: userData.avatar_url, email: userData.email } as GraphQLUser;
    },
  },
};
