import React, { useState, useEffect, useMemo } from 'react';
import {
  Button,
  Checkbox,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Input,
  ModalBody,
  ModalFooter,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  Select,
  Stack,
  Textarea,
  useToast,
  Spinner,
  Alert,
  AlertIcon,
} from '@chakra-ui/react';
import { usePeopleStore, Person } from '../stores/usePeopleStore';
import { useOrganizationsStore, Organization } from '../stores/useOrganizationsStore';
import type {
  PersonInput,
  CustomFieldValueInput,
  CustomFieldDefinition,
  CustomFieldEntityType,
  CustomFieldType as GQLCustomFieldType,
} from '../generated/graphql/graphql';
import { useCustomFieldDefinitionStore } from '../stores/useCustomFieldDefinitionStore';

interface EditPersonFormProps {
  person: Person;
  onClose: () => void;
  onSuccess: () => void;
}

const EditPersonForm: React.FC<EditPersonFormProps> = ({ person, onClose, onSuccess }) => {
  const [formData, setFormData] = useState<PersonInput>({
    first_name: person.first_name,
    last_name: person.last_name,
    email: person.email,
    phone: person.phone,
    notes: person.notes,
    organization_id: person.organization_id,
    customFields: [],
  });
  const [customFieldData, setCustomFieldData] = useState<Record<string, any>>({});
  const [isLoading, setIsLoading] = useState(false);
  const toast = useToast();

  const { 
    organizations, 
    organizationsLoading: orgLoading, 
    organizationsError: orgError,
    fetchOrganizations 
  } = useOrganizationsStore();

  const { updatePerson: updatePersonAction, peopleError } = usePeopleStore();

  const allDefinitions = useCustomFieldDefinitionStore(state => state.definitions);
  const definitionsLoading = useCustomFieldDefinitionStore(state => state.loading);
  const definitionsError = useCustomFieldDefinitionStore(state => state.error);
  const fetchDefinitions = useCustomFieldDefinitionStore(state => state.fetchCustomFieldDefinitions);

  const [localError, setLocalError] = useState<string | null>(null);
  const [hasAttemptedPersonDefinitionsFetch, setHasAttemptedPersonDefinitionsFetch] = useState(false);

  // Memoize personCustomFieldDefinitions
  const personCustomFieldDefinitions = useMemo(() => {
    console.log("[EditPersonForm] Recalculating personCustomFieldDefinitions via useMemo.");
    return allDefinitions.filter(
      d => d.entityType === 'PERSON' && d.isActive
    );
  }, [allDefinitions]); // Dependency is allDefinitions from the store

  useEffect(() => {
    if (Array.isArray(organizations) && !organizations.length && !orgLoading) {
      fetchOrganizations();
    }
    if (!definitionsLoading && !definitionsError && !hasAttemptedPersonDefinitionsFetch && !allDefinitions.some(d => d.entityType === 'PERSON')) {
      console.log("[EditPersonForm] Attempting to fetch PERSON definitions.");
      fetchDefinitions('PERSON' as CustomFieldEntityType).finally(() => {
        setHasAttemptedPersonDefinitionsFetch(true);
      });
    }
  }, [organizations, orgLoading, fetchOrganizations, definitionsLoading, definitionsError, allDefinitions, fetchDefinitions, hasAttemptedPersonDefinitionsFetch]);

  useEffect(() => {
    setFormData({
      first_name: person.first_name,
      last_name: person.last_name,
      email: person.email,
      phone: person.phone,
      notes: person.notes,
      organization_id: person.organization_id,
      customFields: [],
    });
    setLocalError(null);

    console.log('[EditPersonForm] useEffect for customFieldData triggered.');
    console.log('[EditPersonForm] person.id:', person.id);
    console.log('[EditPersonForm] person.customFieldValues:', JSON.stringify(person.customFieldValues, null, 2));
    console.log('[EditPersonForm] personCustomFieldDefinitions:', JSON.stringify(personCustomFieldDefinitions, null, 2));
    console.log('[EditPersonForm] definitionsLoading:', definitionsLoading);
    console.log('[EditPersonForm] hasAttemptedPersonDefinitionsFetch:', hasAttemptedPersonDefinitionsFetch);
    console.log('[EditPersonForm] Calculated personCustomFieldDefinitions length:', personCustomFieldDefinitions.length);

    // Condition to populate customFieldData:
    // 1. Definitions are not currently loading.
    // 2. EITHER a fetch for definitions has been attempted OR person-specific definitions are already available.
    // 3. The person object has custom field values.
    // 4. There are applicable custom field definitions to map against.
    const canPopulate = 
      !definitionsLoading && 
      (hasAttemptedPersonDefinitionsFetch || personCustomFieldDefinitions.length > 0) && 
      person.customFieldValues && 
      person.customFieldValues.length > 0 &&
      personCustomFieldDefinitions && // Ensure this isn't prematurely empty if hasAttempted is false but defs are there
      personCustomFieldDefinitions.length > 0;

    if (canPopulate) {
      console.log('[EditPersonForm] Conditions met to populate customFieldData.');
      const initialCustomData: Record<string, any> = {};
      person.customFieldValues.forEach(cfv => {
        const def = personCustomFieldDefinitions.find(d => d.id === cfv.definition?.id);
        if (def) {
          console.log(`[EditPersonForm] Found matching definition: ID=${def.id}, fieldName=${def.fieldName}, fieldType=${def.fieldType}`);
          let value: any;
          switch (def.fieldType) {
            case 'TEXT': value = cfv.stringValue; break;
            case 'NUMBER': value = cfv.numberValue; break;
            case 'BOOLEAN': value = cfv.booleanValue; break;
            case 'DATE': value = cfv.dateValue; break;
            case 'DROPDOWN': value = cfv.selectedOptionValues?.[0]; break;
            case 'MULTI_SELECT': value = cfv.selectedOptionValues; break;
            default: value = null;
          }
          if (value !== undefined && value !== null) {
            initialCustomData[def.fieldName] = value;
          }
        } else {
          console.warn(`[EditPersonForm] No matching active definition found in personCustomFieldDefinitions for cfv.definition.id: ${cfv.definition?.id}`);
        }
      });
      console.log('[EditPersonForm] Final initialCustomData to be set:', initialCustomData);
      setCustomFieldData(initialCustomData);
    } else {
      console.warn('[EditPersonForm] Conditions NOT met to populate customFieldData. Clearing. Details:',
        {
          definitionsLoading,
          hasAttemptedPersonDefinitionsFetch,
          personCustomFieldValuesCount: person.customFieldValues?.length,
          personCustomFieldDefinitionsCount: personCustomFieldDefinitions?.length
        }
      );
      setCustomFieldData({}); 
    }
  }, [person, person.customFieldValues, personCustomFieldDefinitions, definitionsLoading, hasAttemptedPersonDefinitionsFetch]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name === 'organization_id' && value === '') {
        setFormData(prev => ({ ...prev, [name]: null }));
    } else {
        setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleCustomFieldChange = (fieldName: string, value: any, type: GQLCustomFieldType) => {
    setCustomFieldData(prev => ({
      ...prev,
      [fieldName]: type === 'BOOLEAN' ? (value as React.ChangeEvent<HTMLInputElement>).target.checked : value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setLocalError(null);

    if (!formData.first_name && !formData.last_name && !formData.email) {
      setLocalError('Must have first name, last name, or email.');
      setIsLoading(false);
      return;
    }

    let accumulatedCustomFieldErrors = '';

    const processedCustomFields: CustomFieldValueInput[] = personCustomFieldDefinitions
      .map(def => {
        let fieldHasError = false;
        const rawValue = customFieldData[def.fieldName];

        if (rawValue === undefined && rawValue === null && !def.isRequired) return null;

        if (def.isRequired && (rawValue === undefined || rawValue === null || String(rawValue).trim() === '')) {
            accumulatedCustomFieldErrors += `Field '${def.fieldLabel}' is required.\n`;
            fieldHasError = true;
        }

        const cfInput: CustomFieldValueInput = { definitionId: def.id };

        if (fieldHasError) return null;

        if (rawValue === null || rawValue === '') {
            switch (def.fieldType) {
                case 'TEXT': cfInput.stringValue = null; break;
                case 'NUMBER': cfInput.numberValue = null; break;
                case 'BOOLEAN': cfInput.booleanValue = false; break;
                case 'DATE': cfInput.dateValue = null; break;
                case 'DROPDOWN': cfInput.selectedOptionValues = null; break;
                case 'MULTI_SELECT': cfInput.selectedOptionValues = null; break;
            }
        } else if (rawValue !== undefined) {
            switch (def.fieldType) {
                case 'TEXT': cfInput.stringValue = String(rawValue); break;
                case 'NUMBER':
                    const num = parseFloat(String(rawValue));
                    if (!isNaN(num)) {
                        cfInput.numberValue = num;
                    } else {
                        accumulatedCustomFieldErrors += `Invalid number for '${def.fieldLabel}'.\n`;
                        fieldHasError = true;
                    }
                    break;
                case 'BOOLEAN': cfInput.booleanValue = Boolean(rawValue); break;
                case 'DATE': cfInput.dateValue = rawValue; break;
                case 'DROPDOWN': cfInput.selectedOptionValues = [String(rawValue)]; break;
                case 'MULTI_SELECT': 
                    cfInput.selectedOptionValues = Array.isArray(rawValue) ? rawValue.map(String) : (rawValue ? [String(rawValue)] : []);
                    break;
            }
        }

        if (fieldHasError) return null;

        return cfInput;
      })
      .filter(Boolean) as CustomFieldValueInput[];

    if (accumulatedCustomFieldErrors) {
        setLocalError(accumulatedCustomFieldErrors.trim());
        setIsLoading(false);
        return;
    }

    const mutationInput: PersonInput = {
      first_name: formData.first_name || null,
      last_name: formData.last_name || null,
      email: formData.email || null,
      phone: formData.phone || null,
      notes: formData.notes || null,
      organization_id: formData.organization_id || null,
      customFields: processedCustomFields,
    };

    try {
      const updatedPerson = await updatePersonAction(person.id, mutationInput);

      if (updatedPerson) {
        toast({
          title: 'Person Updated',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
        onSuccess();
        onClose();
      } else {
        setLocalError(peopleError || 'Failed to update person. Please try again.');
        toast({ title: 'Error', description: peopleError || 'Failed to update person.', status: 'error', duration: 5000, isClosable: true });
      }

    } catch (error: unknown) {
      console.error(`Failed to update person ${person.id}:`, error);
      let message = 'An unexpected error occurred.';
      if (error instanceof Error) {
        message = error.message;
      } else if (typeof error === 'string') {
        message = error;
      }
      setLocalError(message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <ModalBody>
        {(localError || peopleError) && (
             <Alert status="error" mb={4} whiteSpace="pre-wrap">
                <AlertIcon />
                {localError || peopleError}
            </Alert>
          )}
        <Stack spacing={4}>
          <FormControl isInvalid={!!localError && (!formData.first_name && !formData.last_name && !formData.email)}>
            <FormLabel>First Name</FormLabel>
            <Input name="first_name" value={formData.first_name || ''} onChange={handleChange} />
          </FormControl>
          <FormControl isInvalid={!!localError && (!formData.first_name && !formData.last_name && !formData.email)}>
            <FormLabel>Last Name</FormLabel>
            <Input name="last_name" value={formData.last_name || ''} onChange={handleChange} />
          </FormControl>
          <FormControl isInvalid={!!localError && (!formData.first_name && !formData.last_name && !formData.email)}>
            <FormLabel>Email</FormLabel>
            <Input type="email" name="email" value={formData.email || ''} onChange={handleChange} />
            {!!localError && (!formData.first_name && !formData.last_name && !formData.email) && <FormErrorMessage>{localError}</FormErrorMessage>}
          </FormControl>
          <FormControl>
            <FormLabel>Phone</FormLabel>
            <Input type="tel" name="phone" value={formData.phone || ''} onChange={handleChange} />
          </FormControl>

          <FormControl>
            <FormLabel>Organization</FormLabel>
            {orgLoading && <Spinner size="sm" />}
            {orgError && (
                <Alert status="error" size="sm">
                    <AlertIcon />
                    {orgError}
                </Alert>
            )}
            {!orgLoading && !orgError && (
                <Select 
                    name="organization_id" 
                    value={formData.organization_id || ''} 
                    onChange={handleChange}
                    placeholder="Select organization (optional)"
                    isDisabled={!Array.isArray(organizations) || organizations.length === 0}
                >
                    {Array.isArray(organizations) && organizations.map(org => (
                        <option key={org.id} value={org.id}>{org.name}</option>
                    ))}
                </Select>
            )}
            {Array.isArray(organizations) && organizations.length === 0 && !orgLoading && <FormErrorMessage>No organizations found. Create one first.</FormErrorMessage>}
          </FormControl>

          <FormControl>
            <FormLabel>Notes</FormLabel>
            <Textarea name="notes" value={formData.notes || ''} onChange={handleChange} />
          </FormControl>

          {definitionsLoading && <Spinner />}
          {definitionsError && <Alert status="error"><AlertIcon />Error loading custom fields: {definitionsError}</Alert>}
          {personCustomFieldDefinitions.map((def: CustomFieldDefinition) => (
            <FormControl key={def.id} isRequired={def.isRequired}>
              <FormLabel>{def.fieldLabel}</FormLabel>
              {def.fieldType === 'TEXT' && (
                <Input
                  value={customFieldData[def.fieldName] || ''}
                  onChange={(e) => handleCustomFieldChange(def.fieldName, e.target.value, def.fieldType)}
                />
              )}
              {def.fieldType === 'NUMBER' && (
                <NumberInput
                  value={customFieldData[def.fieldName] || ''}
                  onChange={(valueString) => handleCustomFieldChange(def.fieldName, valueString, def.fieldType)}
                >
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper />
                    <NumberDecrementStepper />
                  </NumberInputStepper>
                </NumberInput>
              )}
              {def.fieldType === 'BOOLEAN' && (
                <Checkbox
                  isChecked={customFieldData[def.fieldName] || false}
                  onChange={(e) => handleCustomFieldChange(def.fieldName, e, def.fieldType)}
                >
                  Enabled
                </Checkbox>
              )}
              {def.fieldType === 'DATE' && (
                <Input
                  type="date"
                  value={customFieldData[def.fieldName] || ''}
                  onChange={(e) => handleCustomFieldChange(def.fieldName, e.target.value, def.fieldType)}
                />
              )}
              {def.fieldType === 'DROPDOWN' && (
                <Select
                  placeholder={`Select ${def.fieldLabel}`}
                  value={customFieldData[def.fieldName] || ''}
                  onChange={(e) => handleCustomFieldChange(def.fieldName, e.target.value, def.fieldType)}
                >
                  {def.dropdownOptions?.map(opt => (
                    <option key={opt.value} value={opt.value}>{opt.label}</option>
                  ))}
                </Select>
              )}
              {def.fieldType === 'MULTI_SELECT' && (
                 <Textarea
                  placeholder={`Enter values for ${def.fieldLabel}, comma-separated`}
                  value={Array.isArray(customFieldData[def.fieldName]) ? customFieldData[def.fieldName].join(', ') : (customFieldData[def.fieldName] || '')} 
                  onChange={(e) => handleCustomFieldChange(def.fieldName, e.target.value.split(',').map(s => s.trim()), def.fieldType)}
                />
              )}
              {localError && def.isRequired && (customFieldData[def.fieldName] === undefined || customFieldData[def.fieldName] === null || customFieldData[def.fieldName] === '') && 
                <FormErrorMessage>{`Field '${def.fieldLabel}' is required.`}</FormErrorMessage>}
            </FormControl>
          ))}
        </Stack>
      </ModalBody>
      <ModalFooter>
        <Button variant="ghost" mr={3} onClick={onClose} disabled={isLoading}>
          Cancel
        </Button>
        <Button colorScheme="blue" type="submit" isLoading={isLoading}>
          Update Person
        </Button>
      </ModalFooter>
    </form>
  );
}

export default EditPersonForm;

import React from 'react'; // Import React
import {
  Box,
  Text,
  HStack,
  Tag,
  IconButton,
  Checkbox,
  Flex,
  Spacer,
  useToast,
  useDisclosure, // Import useDisclosure hook
} from '@chakra-ui/react';
import { EditIcon, DeleteIcon } from '@chakra-ui/icons';
// import { useAppStore, Activity } from '../../stores/useAppStore'; // OLD IMPORT
import { useAppStore } from '../../stores/useAppStore'; // For userPermissions and currentUserId
import { useActivitiesStore, Activity } from '../../stores/useActivitiesStore'; // NEW IMPORT
import EditActivityModal from './EditActivityModal'; // Import the modal
import ConfirmationDialog from '../common/ConfirmationDialog'; // Import ConfirmationDialog

interface ActivityListItemProps {
  activity: Activity; // This Activity type will now come from useActivitiesStore
}

// Helper to format date/time nicely
const formatDateTime = (isoString: string | null | undefined): string => {
  if (!isoString) return 'No due date';
  try {
    return new Date(isoString).toLocaleString(undefined, {
      dateStyle: 'medium',
      timeStyle: 'short',
    });
  } catch (e) {
    console.error('Error formatting date:', isoString, e);
    return 'Invalid date';
  }
};

// Helper to get tag color based on type
const getActivityTypeColor = (type: string): string => {
    switch (type?.toUpperCase()) {
        case 'TASK': return 'blue';
        case 'MEETING': return 'purple';
        case 'CALL': return 'green';
        case 'EMAIL': return 'orange';
        case 'DEADLINE': return 'red';
        default: return 'gray';
    }
}

function ActivityListItem({ activity }: ActivityListItemProps) {
  // Select actions from the new useActivitiesStore
  const { updateActivity, deleteActivity, activitiesError } = useActivitiesStore(); 

  // Fetch permissions and user ID from useAppStore (to be refactored later with useAuthStore)
  const userPermissions = useAppStore((state) => state.userPermissions);
  const currentUserId = useAppStore((state) => state.session?.user.id);

  const toast = useToast();
  const { isOpen: isEditOpen, onOpen: onEditOpen, onClose: onEditClose } = useDisclosure();
  const { isOpen: isConfirmDeleteDialogOpen, onOpen: onConfirmDeleteOpen, onClose: onConfirmDeleteClose } = useDisclosure();
  const [isDeletingViaDialog, setIsDeletingViaDialog] = React.useState(false);

  const handleToggleDone = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const is_done = e.target.checked;
    const success = await updateActivity(activity.id, { is_done });
    if (!success) {
        toast({ title: 'Failed to update activity status', status: 'error', duration: 3000, isClosable: true });
    }
  };

  const handleDeleteClick = async () => {
    onConfirmDeleteOpen();
  };

  const handleConfirmDelete = async () => {
    setIsDeletingViaDialog(true);
    const success = await deleteActivity(activity.id);
    setIsDeletingViaDialog(false);
    onConfirmDeleteClose();

    if (success) {
         toast({ title: 'Activity deleted.', status: 'success', duration: 3000, isClosable: true });
    } else {
         // Use activitiesError from useActivitiesStore
         toast({ title: 'Error deleting activity', description: activitiesError || 'Unknown error', status: 'error', duration: 3000, isClosable: true });
    }
  };

  const handleEditClick = () => {
      onEditOpen();
  };

  // Determine linked entity for display
  const linkedEntity = activity.deal 
    ? `Deal: ${activity.deal.name}` 
    : activity.person 
    ? `Person: ${activity.person.first_name || ''} ${activity.person.last_name || ''}`.trim()
    : activity.organization
    ? `Org: ${activity.organization.name}`
    : 'No Link';

  return (
    <>
      <Flex 
          alignItems="center" 
          p={3} 
          borderWidth="1px" 
          borderRadius="md" 
          bg={activity.is_done ? 'gray.50' : 'white'}
          _hover={{ bg: activity.is_done ? 'gray.100' : 'gray.50' }}
      >
        <Checkbox 
          isChecked={activity.is_done} 
          onChange={handleToggleDone} 
          mr={4} 
          aria-label="Mark activity as done"
          isDisabled={ // New RBAC logic
            !(
              userPermissions?.includes('activity:update_any') ||
              (userPermissions?.includes('activity:update_own') && activity.user_id === currentUserId)
            )
          }
        />
        <Box flexGrow={1} opacity={activity.is_done ? 0.6 : 1}>
          <HStack mb={1}>
              <Tag size="sm" colorScheme={getActivityTypeColor(activity.type)}>{activity.type || '-'}</Tag>
              <Text fontWeight="medium">{activity.subject}</Text>
          </HStack>
          <Text fontSize="sm" color="gray.600">Due: {formatDateTime(activity.due_date)}</Text>
          <Text fontSize="xs" color="gray.500">Linked To: {linkedEntity}</Text>
          {activity.notes && <Text fontSize="xs" color="gray.500" mt={1}>Notes: {activity.notes}</Text>}
        </Box>
        <Spacer />
        <HStack spacing={1}>
          <IconButton
            aria-label="Edit activity"
            icon={<EditIcon />}
            size="sm"
            variant="ghost"
            onClick={handleEditClick}
            isDisabled={ // New RBAC logic
                !(
                  userPermissions?.includes('activity:update_any') ||
                  (userPermissions?.includes('activity:update_own') && activity.user_id === currentUserId)
                )
            }
          />
          <IconButton
            aria-label="Delete activity"
            icon={<DeleteIcon />}
            colorScheme="red"
            size="sm"
            variant="ghost"
            onClick={handleDeleteClick}
            isDisabled={ // New RBAC logic
                !(
                    userPermissions?.includes('activity:delete_any') ||
                    (userPermissions?.includes('activity:delete_own') && activity.user_id === currentUserId)
                )
            }
          />
        </HStack>
      </Flex>

      {/* Render the Edit Modal */}
      <EditActivityModal 
        isOpen={isEditOpen} 
        onClose={onEditClose} 
        activity={activity} 
      />

      {/* Confirmation Dialog for Deleting Activity */}
      <ConfirmationDialog 
        isOpen={isConfirmDeleteDialogOpen}
        onClose={onConfirmDeleteClose}
        onConfirm={handleConfirmDelete}
        headerText="Delete Activity"
        bodyText="Are you sure you want to delete this activity? This action cannot be undone."
        confirmButtonText="Delete"
        confirmButtonColorScheme="red"
        isLoading={isDeletingViaDialog} // Use specific loading state for dialog
      />
    </>
  );
}

export default ActivityListItem;

import { useForm, SubmitHandler } from 'react-hook-form';
import React, { useState, useEffect } from 'react';
import {
  Button,
  FormControl,
  FormLabel,
  FormErrorMessage,
  Input,
  Select,
  Textarea,
  Checkbox,
  VStack,
  useToast,
  RadioGroup,
  Radio,
  HStack,
  Spinner,
  Alert,
  AlertIcon,
} from '@chakra-ui/react';
import { useActivitiesStore, GeneratedCreateActivityInput as CreateActivityInput, ActivityType } from '../../stores/useActivitiesStore';
import { usePeopleStore, Person } from '../../stores/usePeopleStore';
import { useDealsStore, Deal } from '../../stores/useDealsStore';
import { useOrganizationsStore, Organization } from '../../stores/useOrganizationsStore';

// Define Activity Types matching GraphQL Enum
const activityTypes = [
  'TASK',
  'MEETING',
  'CALL',
  'EMAIL',
  'DEADLINE',
];

interface CreateActivityFormProps {
  onClose: () => void;
  onSuccess?: () => void; // Optional callback on successful creation
  // Pass pre-filled links if creating from Deal/Person/Org page?
  // initialDealId?: string;
  // initialPersonId?: string;
  // initialOrganizationId?: string;
}

// Use the store's CreateActivityInput for form values
type FormValues = CreateActivityInput;

// Define type for link selection
type LinkType = 'deal' | 'person' | 'organization' | 'none';

function CreateActivityForm({ onClose, onSuccess }: CreateActivityFormProps) {
  // Actions and state from useActivitiesStore
  const { createActivity, activitiesError, activitiesLoading } = useActivitiesStore();

  // People state & actions from usePeopleStore
  const { people, fetchPeople, peopleLoading, peopleError: _peopleError } = usePeopleStore();

  // Deals state & actions from useDealsStore
  const { deals, fetchDeals, dealsLoading, dealsError: _dealsError } = useDealsStore();

  // Organizations state & actions from useOrganizationsStore
  const { organizations, fetchOrganizations, organizationsLoading, organizationsError: _organizationsError } = useOrganizationsStore();

  const toast = useToast();
  const { 
    handleSubmit, 
    register, 
    formState: { errors, isSubmitting },
    reset, 
    setValue, 
  } = useForm<FormValues>({
      defaultValues: {
          type: activityTypes[0] as ActivityType,
          subject: '',
          due_date: null,
          notes: '',
          is_done: false,
          deal_id: null,
          person_id: null,
          organization_id: null,
      }
  });

  // State for the selected link type
  const [selectedLinkType, setSelectedLinkType] = useState<LinkType>('none');

  // Fetch related entities when the form mounts (or modal opens)
  useEffect(() => {
    // Fetch data for dropdowns when the component mounts.
    // The stores themselves should ideally prevent redundant fetches if data is already loaded or loading.
    fetchDeals(); 
    fetchPeople();
    fetchOrganizations();
    // Dependency array includes the fetch functions. These should be stable references from Zustand.
    // If they were unstable, this could loop. Assuming they are stable.
  }, [fetchDeals, fetchPeople, fetchOrganizations]);

  // Clear other link IDs when radio selection changes
  const handleLinkTypeChange = (nextValue: string) => {
      const linkType = nextValue as LinkType;
      setSelectedLinkType(linkType);
      // Clear other fields when changing type
      if (linkType !== 'deal') setValue('deal_id', null);
      if (linkType !== 'person') setValue('person_id', null);
      if (linkType !== 'organization') setValue('organization_id', null);
  };

  const onSubmit: SubmitHandler<FormValues> = async (values) => {
    console.log('Submitting activity:', values);

    // Ensure at least one link is present (client-side check mimicking backend)
    if (!values.deal_id && !values.person_id && !values.organization_id) {
        toast({ title: 'Link Required', description: 'Please link the activity to a Deal, Person, or Organization.', status: 'warning', duration: 4000, isClosable: true });
        return; // Prevent submission
    }

    // Convert empty strings to null for optional fields if necessary (esp. links)
    const submissionData = { ...values };
    if (!submissionData.deal_id) submissionData.deal_id = null;
    if (!submissionData.person_id) submissionData.person_id = null;
    if (!submissionData.organization_id) submissionData.organization_id = null;
    if (!submissionData.notes) submissionData.notes = null;

    // Format due_date to ISO 8601 if it exists
    if (submissionData.due_date) {
      try {
        // Create a Date object from the local datetime string
        const date = new Date(submissionData.due_date);
        // Check if the date is valid before converting
        if (!isNaN(date.getTime())) {
          submissionData.due_date = date.toISOString();
        } else {
          // Handle invalid date input from the browser - could clear or keep original string based on desired UX
          console.warn("Invalid date string from datetime-local input:", submissionData.due_date);
          // Setting to null to avoid sending invalid string to backend
          submissionData.due_date = null; 
        }
      } catch (error) {
          console.error("Error parsing date:", error);
          // Handle potential errors during Date construction/conversion, set to null
          submissionData.due_date = null;
      }
    } else {
       // Ensure it's explicitly null if not provided or cleared
       submissionData.due_date = null;
    }

    const createdActivity = await createActivity(submissionData);

    if (createdActivity) {
      toast({ title: 'Activity created.', status: 'success', duration: 3000, isClosable: true });
      reset(); // Reset form on success
      onSuccess?.(); // Call optional success callback (e.g., refresh list)
      onClose(); // Close modal
    } else {
      // Error toast is likely handled by the store hook or generic error boundary
      // but we can add a specific one here if desired.
      toast({ title: 'Failed to create activity', description: activitiesError || 'Please check the details and try again.', status: 'error', duration: 5000, isClosable: true });
    }
  };

  // Loading state for dropdowns
  const isLoadingLinks = dealsLoading || peopleLoading || organizationsLoading;

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <VStack spacing={4} p={4}>
        {/* Optionally display activityError if it means form submission error */}
        {activitiesError && (
            <Alert status="error" mb={4}>
                <AlertIcon />
                {activitiesError}
            </Alert>
        )}
        <FormControl isInvalid={!!errors.subject}>
          <FormLabel htmlFor='subject'>Subject</FormLabel>
          <Input
            id='subject'
            {...register('subject', { required: 'Subject is required' })}
          />
          <FormErrorMessage>{errors.subject?.message}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.type}>
            <FormLabel htmlFor='type'>Type</FormLabel>
            <Select id='type' {...register('type', { required: 'Type is required' })} defaultValue={activityTypes[0] as ActivityType}>
                {activityTypes.map(type => (
                    <option key={type} value={type}>{type}</option>
                ))}
            </Select>
            <FormErrorMessage>{errors.type?.message}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.due_date}>
          <FormLabel htmlFor='due_date'>Due Date/Time</FormLabel>
          <Input
            id='due_date'
            type='datetime-local' // Use datetime-local for combined date and time
            {...register('due_date')}
          />
          <FormErrorMessage>{errors.due_date?.message as string}</FormErrorMessage>
        </FormControl>

        {/* --- Linked Entity Selection --- */}
        <FormControl isInvalid={!!errors.deal_id || !!errors.person_id || !!errors.organization_id}>
             <FormLabel>Linked To</FormLabel>
             <RadioGroup onChange={handleLinkTypeChange} value={selectedLinkType}>
                 <HStack spacing='20px' mb={2}>
                     <Radio value='deal'>Deal</Radio>
                     <Radio value='person'>Person</Radio>
                     <Radio value='organization'>Organization</Radio>
                     <Radio value='none' isDisabled={true} hidden={true}>None</Radio> {/* Hidden option for default/cleared */} 
                 </HStack>
             </RadioGroup>

             {isLoadingLinks && <Spinner size="sm" my={2}/>}

             {!isLoadingLinks && selectedLinkType === 'deal' && (
                 <Select 
                    id='deal_id' 
                    placeholder='Select Deal' 
                    {...register('deal_id')} // Register deal_id
                >
                     {deals.map((deal: Deal) => (
                        <option key={deal.id} value={deal.id}>{deal.name}</option>
                     ))}
                 </Select>
             )}
            {!isLoadingLinks && selectedLinkType === 'person' && (
                 <Select 
                    id='person_id' 
                    placeholder='Select Person' 
                    {...register('person_id')} // Register person_id
                 >
                     {people.map((person: Person) => (
                        <option key={person.id} value={person.id}>
                            {[person.first_name, person.last_name].filter(Boolean).join(' ') || person.email}
                        </option>
                     ))}
                 </Select>
             )}
             {!isLoadingLinks && selectedLinkType === 'organization' && (
                 <Select 
                    id='organization_id' 
                    placeholder='Select Organization' 
                    {...register('organization_id')} // Register organization_id
                 >
                     {organizations.map((org: Organization) => (
                        <option key={org.id} value={org.id}>{org.name}</option>
                     ))}
                 </Select>
             )}
             {(errors.deal_id || errors.person_id || errors.organization_id) && 
                <FormErrorMessage>Please select a linked entity if a type is chosen.</FormErrorMessage> }
        </FormControl>
        {/* --- End Linked Entity Selection --- */}

        <FormControl isInvalid={!!errors.notes}>
          <FormLabel htmlFor='notes'>Notes</FormLabel>
          <Textarea id='notes' {...register('notes')} />
          <FormErrorMessage>{errors.notes?.message}</FormErrorMessage>
        </FormControl>

        <FormControl>
          <Checkbox {...register('is_done')}>Mark as Done</Checkbox>
        </FormControl>

        <Button type="submit" colorScheme="blue" isLoading={isSubmitting || activitiesLoading}>
          Create Activity
        </Button>
      </VStack>
    </form>
  );
}

