import React, { useEffect, useState } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import {
  Button,
  FormControl,
  FormLabel,
  FormErrorMessage,
  Input,
  Select,
  Textarea,
  Checkbox,
  VStack,
  useToast,
  Alert,
  AlertIcon,
  RadioGroup,
  Radio,
  HStack,
  Spinner
} from '@chakra-ui/react';
import { 
    useActivitiesStore, 
    GeneratedUpdateActivityInput as UpdateActivityInput,
    ActivityType, 
    Activity 
} from '../../stores/useActivitiesStore';
import { useDealsStore, Deal } from '../../stores/useDealsStore';
import { usePeopleStore, Person } from '../../stores/usePeopleStore';
import { useOrganizationsStore, Organization } from '../../stores/useOrganizationsStore';

// Define Activity Types matching GraphQL Enum
const activityTypes = [
  'TASK',
  'MEETING',
  'CALL',
  'EMAIL',
  'DEADLINE',
];

interface EditActivityFormProps {
  activity: Activity; // Use Activity type from store
  onClose: () => void;
  onSuccess?: () => void;
}

// Define type for link selection (same as CreateActivityForm)
type LinkType = 'deal' | 'person' | 'organization' | 'none';

function EditActivityForm({ activity, onClose, onSuccess }: EditActivityFormProps) {
  // Actions and state from useActivitiesStore
  const { updateActivity, activitiesError, activitiesLoading } = useActivitiesStore();
  const toast = useToast();

  // Fetching data for linked entities
  const { deals, fetchDeals, dealsLoading } = useDealsStore();
  const { people, fetchPeople, peopleLoading } = usePeopleStore();
  const { organizations, fetchOrganizations, organizationsLoading } = useOrganizationsStore();

  const getInitialLinkType = (): LinkType => {
    if (activity.deal_id) return 'deal';
    if (activity.person_id) return 'person';
    if (activity.organization_id) return 'organization';
    return 'none'; // Should ideally not be 'none' for an existing activity if links are mandatory
  };

  const [selectedLinkType, setSelectedLinkType] = useState<LinkType>(getInitialLinkType());

  const { 
    handleSubmit, 
    register, 
    formState: { errors, isSubmitting }, 
    reset, 
    setValue
  } = useForm<UpdateActivityInput>({
      defaultValues: {
      type: activity.type,
          subject: activity.subject,
      due_date: activity.due_date ? new Date(activity.due_date).toISOString().substring(0, 16) : null,
      notes: activity.notes || '',
          is_done: activity.is_done,
          deal_id: activity.deal_id,
          person_id: activity.person_id,
          organization_id: activity.organization_id,
      }
  });

  // Reset form if activity prop changes (e.g. modal is reused for different activities)
  useEffect(() => {
    reset({
      type: activity.type,
      subject: activity.subject,
      due_date: activity.due_date ? new Date(activity.due_date).toISOString().substring(0, 16) : null,
      notes: activity.notes || '',
      is_done: activity.is_done,
      deal_id: activity.deal_id,
      person_id: activity.person_id,
      organization_id: activity.organization_id,
    });
    setSelectedLinkType(getInitialLinkType()); // Reset link type when activity changes
  }, [activity, reset]);

  // Fetch related entities
  useEffect(() => {
    if (deals.length === 0 && !dealsLoading) fetchDeals();
    if (people.length === 0 && !peopleLoading) fetchPeople();
    if (organizations.length === 0 && !organizationsLoading) fetchOrganizations();
  }, [fetchDeals, fetchPeople, fetchOrganizations, deals, people, organizations, dealsLoading, peopleLoading, organizationsLoading]);

  const handleLinkTypeChange = (nextValue: string) => {
    const linkType = nextValue as LinkType;
    setSelectedLinkType(linkType);
    setValue('deal_id', linkType === 'deal' ? (activity.deal_id || null) : null, { shouldDirty: true });
    setValue('person_id', linkType === 'person' ? (activity.person_id || null) : null, { shouldDirty: true });
    setValue('organization_id', linkType === 'organization' ? (activity.organization_id || null) : null, { shouldDirty: true });
  };

  const onSubmit: SubmitHandler<UpdateActivityInput> = async (values) => {
    const submissionData: UpdateActivityInput = { ...values }; 

    if (!submissionData.deal_id && !submissionData.person_id && !submissionData.organization_id) {
        toast({ title: 'Link Required', description: 'Activity must be linked to a Deal, Person, or Organization.', status: 'warning', duration: 4000, isClosable: true });
        return;
    }

    if (submissionData.notes === '') submissionData.notes = null;
    if (submissionData.due_date) {
                    try {
        const date = new Date(submissionData.due_date);
        submissionData.due_date = !isNaN(date.getTime()) ? date.toISOString() : null;
      } catch { submissionData.due_date = null; }
                        } else {
      submissionData.due_date = null;
    }

    const updatedActivity = await updateActivity(activity.id, submissionData);
    if (updatedActivity) {
      toast({ title: 'Activity updated.', status: 'success', duration: 3000, isClosable: true });
      onSuccess?.();
      onClose();
    } else {
      toast({ title: 'Failed to update activity', description: activitiesError || 'Please check details and try again.', status: 'error', duration: 5000, isClosable: true });
    }
  };

  const isLoadingLinks = dealsLoading || peopleLoading || organizationsLoading;

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <VStack spacing={4} p={4}>
        {activitiesError && (
            <Alert status="error" mb={4}>
                <AlertIcon />
                {activitiesError}
            </Alert>
        )}

        <FormControl isInvalid={!!errors.subject}>
          <FormLabel htmlFor='subject'>Subject</FormLabel>
          <Input
            id='subject'
            {...register('subject', { required: 'Subject is required' })}
          />
          <FormErrorMessage>{errors.subject?.message}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.type}>
            <FormLabel htmlFor='type'>Type</FormLabel>
            <Select id='type' {...register('type', { required: 'Type is required' })}>
                {activityTypes.map(type => (
                    <option key={type} value={type}>{type}</option>
                ))}
            </Select>
            <FormErrorMessage>{errors.type?.message}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.due_date}>
          <FormLabel htmlFor='due_date'>Due Date/Time</FormLabel>
          <Input
            id='due_date'
            type='datetime-local'
            {...register('due_date')}
          />
          <FormErrorMessage>{errors.due_date?.message as string}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.deal_id || !!errors.person_id || !!errors.organization_id}>
             <FormLabel>Linked To</FormLabel>
             <RadioGroup onChange={handleLinkTypeChange} value={selectedLinkType}>
                 <HStack spacing='20px' mb={2}>
                     <Radio value='deal'>Deal</Radio>
                     <Radio value='person'>Person</Radio>
                     <Radio value='organization'>Organization</Radio>
                 </HStack>
             </RadioGroup>

             {isLoadingLinks && <Spinner size="sm" my={2}/>}

             {!isLoadingLinks && selectedLinkType === 'deal' && (
                 <Select id='deal_id' placeholder='Select Deal' {...register('deal_id')} defaultValue={activity.deal_id || ''}>
                     {deals.map((deal: Deal) => (<option key={deal.id} value={deal.id}>{deal.name}</option>))}
                 </Select>
             )}
            {!isLoadingLinks && selectedLinkType === 'person' && (
                 <Select id='person_id' placeholder='Select Person' {...register('person_id')} defaultValue={activity.person_id || ''}>
                     {people.map((person: Person) => (<option key={person.id} value={person.id}>{`${person.first_name || ''} ${person.last_name || ''}`.trim() || person.email}</option>))}
                 </Select>
             )}
             {!isLoadingLinks && selectedLinkType === 'organization' && (
                 <Select id='organization_id' placeholder='Select Organization' {...register('organization_id')} defaultValue={activity.organization_id || ''}>
                     {organizations.map((org: Organization) => (<option key={org.id} value={org.id}>{org.name}</option>))}
                 </Select>
             )}
             {(errors.deal_id && selectedLinkType ==='deal' || errors.person_id && selectedLinkType ==='person' || errors.organization_id && selectedLinkType ==='organization') && 
                <FormErrorMessage>Please select a linked entity.</FormErrorMessage> }
        </FormControl>

        <FormControl isInvalid={!!errors.notes}>
          <FormLabel htmlFor='notes'>Notes</FormLabel>
          <Textarea
            id='notes'
            {...register('notes')}
          />
          <FormErrorMessage>{errors.notes?.message}</FormErrorMessage>
        </FormControl>

        <FormControl>
          <Checkbox id='is_done' {...register('is_done')}>
            Mark as Done
          </Checkbox>
        </FormControl>

        <Button type="submit" colorScheme="blue" isLoading={isSubmitting || activitiesLoading}>
          Update Activity
        </Button>
      </VStack>
    </form>
  );
}

export default EditActivityForm;

import React, { useEffect } from 'react';
import { useForm, Controller, useFieldArray } from 'react-hook-form';
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  FormErrorMessage,
  Input,
  Select,
  Checkbox,
  VStack,
  Text,
  SimpleGrid,
  IconButton,
  HStack,
} from '@chakra-ui/react';
import { AddIcon, DeleteIcon } from '@chakra-ui/icons';
import {
  CustomFieldDefinitionInput,
  CustomFieldEntityType,
  CustomFieldType,
  CustomFieldOptionInput,
} from '../../../generated/graphql/graphql';

export interface CustomFieldDefinitionFormValues extends Omit<CustomFieldDefinitionInput, 'dropdownOptions' | 'entityType'> {
  entityType: CustomFieldEntityType;
  dropdownOptions?: CustomFieldOptionInput[];
}

interface CustomFieldDefinitionFormProps {
  onSubmit: (values: CustomFieldDefinitionInput) => Promise<void>;
  initialValues?: Partial<CustomFieldDefinitionFormValues>;
  entityType: CustomFieldEntityType;
  isSubmitting: boolean;
}

const CustomFieldDefinitionForm: React.FC<CustomFieldDefinitionFormProps> = ({
  onSubmit,
  initialValues,
  entityType,
  isSubmitting,
}) => {
  const {
    handleSubmit,
    register,
    control,
    watch,
    reset,
    formState: { errors },
  } = useForm<CustomFieldDefinitionFormValues>({
    defaultValues: {
      ...initialValues,
      entityType: entityType,
      isRequired: initialValues?.isRequired ?? false,
      displayOrder: initialValues?.displayOrder ?? 0,
      fieldType: initialValues?.fieldType || CustomFieldType.Text,
      dropdownOptions: initialValues?.dropdownOptions || [],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "dropdownOptions",
  });

  const isEditMode = !!initialValues?.fieldName;

  useEffect(() => {
    const defaultVals = {
      ...initialValues,
      entityType: entityType,
      isRequired: initialValues?.isRequired ?? false,
      displayOrder: initialValues?.displayOrder ?? 0,
      fieldType: initialValues?.fieldType || CustomFieldType.Text,
      dropdownOptions: initialValues?.dropdownOptions || [],
    };
    reset(defaultVals);
  }, [initialValues, entityType, reset]);

  const fieldTypeWatch = watch('fieldType');

  const onFormSubmit = (values: CustomFieldDefinitionFormValues) => {
    const submissionValues: CustomFieldDefinitionInput = {
      entityType: entityType,
      fieldName: values.fieldName,
      fieldLabel: values.fieldLabel,
      fieldType: values.fieldType,
      isRequired: values.isRequired,
      displayOrder: values.displayOrder,
      dropdownOptions: (values.fieldType === CustomFieldType.Dropdown || values.fieldType === CustomFieldType.MultiSelect) && values.dropdownOptions && values.dropdownOptions.length > 0 
        ? values.dropdownOptions 
        : null,
    };
    return onSubmit(submissionValues);
  };

  return (
    <form onSubmit={handleSubmit(onFormSubmit)}>
      <VStack spacing={4} align="stretch">
        <FormControl isInvalid={!!errors.fieldLabel} isRequired>
          <FormLabel htmlFor="fieldLabel">Field Label</FormLabel>
          <Input
            id="fieldLabel"
            {...register('fieldLabel', { required: 'Field Label is required' })}
          />
          <FormErrorMessage>{errors.fieldLabel?.message}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.fieldName} isRequired>
          <FormLabel htmlFor="fieldName">Field Name (Internal)</FormLabel>
          <Input
            id="fieldName"
            {...register('fieldName', {
              required: 'Field Name is required',
              pattern: {
                value: /^[a-zA-Z0-9_]+$/,
                message: 'Field Name can only contain letters, numbers, and underscores.',
              },
            })}
            placeholder="e.g., contact_source or deal_priority"
            isDisabled={isEditMode}
          />
          <FormErrorMessage>{errors.fieldName?.message}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.fieldType} isRequired>
          <FormLabel htmlFor="fieldType">Field Type</FormLabel>
          <Controller
            name="fieldType"
            control={control}
            rules={{ required: 'Field Type is required' }}
            defaultValue={CustomFieldType.Text}
            render={({ field }) => (
              <Select {...field} id="fieldType" isDisabled={isEditMode}>
                {Object.values(CustomFieldType).map((type) => (
                  <option key={type} value={type}>
                    {type}
                  </option>
                ))}
              </Select>
            )}
          />
          <FormErrorMessage>{errors.fieldType?.message}</FormErrorMessage>
        </FormControl>

        {(fieldTypeWatch === CustomFieldType.Dropdown || fieldTypeWatch === CustomFieldType.MultiSelect) && (
          <Box borderWidth="1px" borderRadius="md" p={4}>
            <HStack justifyContent="space-between" mb={2}>
                <FormLabel mb={0}>Dropdown Options</FormLabel>
                <Button leftIcon={<AddIcon />} size="sm" onClick={() => append({ value: '', label: '' })}>
                    Add Option
                </Button>
            </HStack>
            {fields.map((item, index) => (
              <SimpleGrid columns={3} spacing={2} key={item.id} mb={2} alignItems="center">
                <FormControl isInvalid={!!errors.dropdownOptions?.[index]?.value}>
                  <Input
                    {...register(`dropdownOptions.${index}.value` as const, { required: 'Value is required' })}
                    placeholder="Option Value"
                    defaultValue={item.value} 
                  />
                  <FormErrorMessage>{errors.dropdownOptions?.[index]?.value?.message}</FormErrorMessage>
                </FormControl>
                <FormControl isInvalid={!!errors.dropdownOptions?.[index]?.label}>
                  <Input
                    {...register(`dropdownOptions.${index}.label` as const, { required: 'Label is required' })}
                    placeholder="Option Label"
                    defaultValue={item.label}
                  />
                  <FormErrorMessage>{errors.dropdownOptions?.[index]?.label?.message}</FormErrorMessage>
                </FormControl>
                <IconButton
                  aria-label="Remove option"
                  icon={<DeleteIcon />}
                  size="sm"
                  colorScheme="red"
                  onClick={() => remove(index)}
                />
              </SimpleGrid>
            ))}
            {fields.length === 0 && <Text fontSize="sm" color="gray.500">No options defined. Click "Add Option".</Text>}
          </Box>
        )}

        <FormControl isInvalid={!!errors.displayOrder}>
          <FormLabel htmlFor="displayOrder">Display Order</FormLabel>
          <Controller
            name="displayOrder"
            control={control}
            defaultValue={0}
            render={({ field }) => (
              <Input 
                {...field} 
                id="displayOrder" 
                type="number" 
                value={field.value ?? ''}
                onChange={e => field.onChange(parseInt(e.target.value, 10) || 0)} 
              />
            )}
          />
          <FormErrorMessage>{errors.displayOrder?.message}</FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.isRequired}>
          <Checkbox id="isRequired" {...register('isRequired')}>
            Is Required?
          </Checkbox>
          <FormErrorMessage>{errors.isRequired?.message}</FormErrorMessage>
        </FormControl>

        <Button mt={4} colorScheme="blue" isLoading={isSubmitting} type="submit">
          {initialValues?.fieldName ? 'Update Definition' : 'Create Definition'}
        </Button>
      </VStack>
    </form>
  );
};

export default CustomFieldDefinitionForm;

import React from 'react';
import {
  Box,
  Text,
  Spinner,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  Button,
  Flex,
  Select,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Tag,
  IconButton,
  HStack,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  useToast,
  AlertDialog,
  AlertDialogBody,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogContent,
  AlertDialogOverlay,
} from '@chakra-ui/react';
import { EditIcon, CheckIcon, NotAllowedIcon, AddIcon } from '@chakra-ui/icons';
import { useCustomFieldDefinitionStore } from '../../../stores/useCustomFieldDefinitionStore';
import {
  CustomFieldEntityType,
  CustomFieldDefinition,
  CustomFieldDefinitionInput,
} from '../../../generated/graphql/graphql';
import CustomFieldDefinitionForm from './CustomFieldDefinitionForm';

const CustomFieldDefinitionList: React.FC = () => {
  const {
    definitions,
    loading,
    error,
    fetchCustomFieldDefinitions,
    createCustomFieldDefinition,
    updateCustomFieldDefinition, // For edit later
    deactivateCustomFieldDefinition,
    reactivateCustomFieldDefinition,
  } = useCustomFieldDefinitionStore();

  const { isOpen, onOpen, onClose } = useDisclosure();
  const [isSubmitting, setIsSubmitting] = React.useState(false);
  const toast = useToast();

  // TODO: Add state for currentDefinitionToEdit: CustomFieldDefinition | null
  const [currentDefinitionToEdit, setCurrentDefinitionToEdit] = React.useState<CustomFieldDefinition | null>(null);

  const { isOpen: isAlertOpen, onOpen: onAlertOpen, onClose: onAlertClose } = useDisclosure();
  const [actionToConfirm, setActionToConfirm] = React.useState<{ id: string; type: 'deactivate' | 'reactivate'; label: string} | null>(null);
  const cancelRef = React.useRef<HTMLButtonElement>(null);

  const [selectedEntityType, setSelectedEntityType] = React.useState<CustomFieldEntityType>(
    CustomFieldEntityType.Deal
  );

  React.useEffect(() => {
    fetchCustomFieldDefinitions(selectedEntityType, true);
  }, [selectedEntityType, fetchCustomFieldDefinitions]);

  const handleEntityTypeChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedEntityType(event.target.value as CustomFieldEntityType);
  };

  const handleOpenCreateModal = () => {
    // TODO: Set currentDefinitionToEdit(null) for create mode
    setCurrentDefinitionToEdit(null);
    onOpen();
  };

  const handleOpenEditModal = (definition: CustomFieldDefinition) => {
    setCurrentDefinitionToEdit(definition);
    onOpen();
  };

  const handleFormSubmit = async (submissionInput: CustomFieldDefinitionInput) => {
    setIsSubmitting(true);
    try {
      // console.log('Submitting Custom Field Definition:', submissionInput, 'Edit Mode:', !!currentDefinitionToEdit); 

      let result: CustomFieldDefinition | null = null;
      if (currentDefinitionToEdit && currentDefinitionToEdit.id) {
        result = await updateCustomFieldDefinition(currentDefinitionToEdit.id, submissionInput);
        if (result) {
          toast({
            title: 'Custom Field Definition Updated',
            description: `Successfully updated field: ${result.fieldLabel}`,
            status: 'success',
            duration: 5000,
            isClosable: true,
          });
        }
      } else {
        result = await createCustomFieldDefinition(submissionInput);
        if (result) {
          toast({
            title: 'Custom Field Definition Created',
            description: `Successfully created field: ${result.fieldLabel}`,
            status: 'success',
            duration: 5000,
            isClosable: true,
          });
        }
      }

      if (result) {
        onClose(); // Close modal on success
        setCurrentDefinitionToEdit(null); // Reset edit state
      } else {
        // Error might be in storeError, or result is null without throwing
        throw new Error(useCustomFieldDefinitionStore.getState().error || 
          (currentDefinitionToEdit ? 'Failed to update definition.' : 'Failed to create definition.')
        );
      }
    } catch (err: any) {
      toast({
        title: currentDefinitionToEdit ? 'Error Updating Definition' : 'Error Creating Definition',
        description: err.message || 'An unexpected error occurred.',
        status: 'error',
        duration: 9000,
        isClosable: true,
      });
    } finally {
      setIsSubmitting(false);
      onClose(); // Ensure modal always closes
      setCurrentDefinitionToEdit(null); // Also ensure edit state is reset
      fetchCustomFieldDefinitions(selectedEntityType, true); // Refetch definitions
    }
  };

  const handleDeactivate = async (id: string, label: string) => {
    setActionToConfirm({ id, type: 'deactivate', label });
    onAlertOpen();
  };

  const handleReactivate = async (id: string, label: string) => {
    setActionToConfirm({ id, type: 'reactivate', label });
    onAlertOpen();
  };

  const confirmAction = async () => {
    if (!actionToConfirm) return;

    const { id, type, label } = actionToConfirm;
    setIsSubmitting(true); 
    let result: CustomFieldDefinition | null = null;
    let successMessage = '';
    let errorMessage = '';

    try {
      if (type === 'deactivate') {
        result = await deactivateCustomFieldDefinition(id);
        successMessage = `Successfully deactivated ${label}`;
        errorMessage = 'Failed to deactivate definition.';
      } else {
        result = await reactivateCustomFieldDefinition(id);
        successMessage = `Successfully reactivated ${label}`;
        errorMessage = 'Failed to reactivate definition.';
      }

      if (result) {
        toast({
          title: `Definition ${type === 'deactivate' ? 'Deactivated' : 'Reactivated'}`,
          description: successMessage,
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        throw new Error(useCustomFieldDefinitionStore.getState().error || errorMessage);
      }
    } catch (err: any) {
      toast({
        title: `Error ${type === 'deactivate' ? 'Deactivating' : 'Reactivating'} Definition`,
        description: err.message || 'An unexpected error occurred.',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsSubmitting(false);
      onAlertClose();
      setActionToConfirm(null);
    }
  };

  return (
    <Box borderWidth="1px" borderRadius="lg" p={6} shadow="sm">
      <Flex justifyContent="space-between" alignItems="center" mb={4}>
        <HStack spacing={3} alignItems="center">
            <Text fontSize="xl" fontWeight="semibold">
            Custom Field Definitions for:
            </Text>
            <Select 
                value={selectedEntityType} 
                onChange={handleEntityTypeChange} 
                width="200px"
                isDisabled={loading || isSubmitting}
            >
                <option value={CustomFieldEntityType.Deal}>Deals</option>
                <option value={CustomFieldEntityType.Person}>People</option>
                <option value={CustomFieldEntityType.Organization}>Organizations</option>
            </Select>
        </HStack>
        <Button 
            leftIcon={<AddIcon />} 
            colorScheme="blue" 
            onClick={handleOpenCreateModal} 
            ml="auto"
            isDisabled={loading || isSubmitting}
        >
          Add New Definition
        </Button>
      </Flex>

      {loading && (
        <Flex justifyContent="center" my={8}>
          <Spinner thickness="4px" speed="0.65s" emptyColor="gray.200" color="blue.500" size="xl" />
        </Flex>
      )}

      {error && (
        <Alert status="error" my={4}>
          <AlertIcon />
          <AlertTitle>Error loading definitions!</AlertTitle>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {!loading && !error && (
        <Table variant="simple" size="sm">
          <Thead>
            <Tr>
              <Th>Label</Th>
              <Th>Name (Internal)</Th>
              <Th>Type</Th>
              <Th>Required</Th>
              <Th>Status</Th>
              <Th>Order</Th>
              <Th>Actions</Th>
            </Tr>
          </Thead>
          <Tbody>
            {definitions.length === 0 && (
              <Tr>
                <Td colSpan={6} textAlign="center">No custom field definitions found for {selectedEntityType.toLowerCase()}.</Td>
              </Tr>
            )}
            {definitions.map((def: CustomFieldDefinition) => (
              <Tr key={def.id}>
                <Td>{def.fieldLabel}</Td>
                <Td><code>{def.fieldName}</code></Td>
                <Td>{def.fieldType}</Td>
                <Td>{def.isRequired ? 'Yes' : 'No'}</Td>
                <Td>
                  <Tag colorScheme={def.isActive ? 'green' : 'red'}>
                    {def.isActive ? 'Active' : 'Inactive'}
                  </Tag>
                </Td>
                <Td>{def.displayOrder}</Td>
                <Td>
                  <HStack spacing={2}>
                    <IconButton 
                      aria-label="Edit definition" 
                      icon={<EditIcon />} 
                      size="sm" 
                      onClick={() => handleOpenEditModal(def)}
                      isDisabled={isSubmitting}
                    />
                    {def.isActive ? (
                      <IconButton 
                        aria-label="Deactivate definition" 
                        icon={<NotAllowedIcon />} 
                        size="sm" 
                        colorScheme="red"
                        onClick={() => handleDeactivate(def.id, def.fieldLabel)}
                        isDisabled={isSubmitting}
                      />
                    ) : (
                      <IconButton 
                        aria-label="Reactivate definition" 
                        icon={<CheckIcon />} 
                        size="sm" 
                        colorScheme="green"
                        onClick={() => handleReactivate(def.id, def.fieldLabel)}
                        isDisabled={isSubmitting}
                      />
                    )}
                  </HStack>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      )}

      <Modal isOpen={isOpen} onClose={onClose} size="xl" closeOnOverlayClick={false}>
        <ModalOverlay />
        <ModalContent>
          {/* TODO: Change header based on edit mode */}
          <ModalHeader>{currentDefinitionToEdit ? 'Edit Custom Field Definition' : 'Add New Custom Field Definition'}</ModalHeader>
          <ModalCloseButton isDisabled={isSubmitting} />
          <ModalBody pb={6}>
            <CustomFieldDefinitionForm 
              entityType={selectedEntityType} 
              onSubmit={handleFormSubmit} 
              isSubmitting={isSubmitting}
              initialValues={currentDefinitionToEdit ? 
                { 
                  ...currentDefinitionToEdit, 
                  // Ensure dropdownOptions is in the format expected by the form if it exists
                  dropdownOptions: currentDefinitionToEdit.dropdownOptions?.map(opt => ({ value: opt.value, label: opt.label })) 
                }
                : { entityType: selectedEntityType } 
              }
              // onCancel={onClose}
            />
          </ModalBody>
          {/* ModalFooter can be removed if form has its own submit/cancel buttons */}
          {/* <ModalFooter>
            <Button onClick={onClose} mr={3} isDisabled={isSubmitting}>Cancel</Button>
            <Button colorScheme="blue" form="custom-field-form" type="submit" isLoading={isSubmitting}>
              Save Definition
            </Button>
          </ModalFooter> */}
        </ModalContent>
      </Modal>

      {actionToConfirm && (
        <AlertDialog
          isOpen={isAlertOpen}
          leastDestructiveRef={cancelRef}
          onClose={() => {
            onAlertClose();
            setActionToConfirm(null);
          }}
          isCentered
        >
          <AlertDialogOverlay>
            <AlertDialogContent>
              <AlertDialogHeader fontSize="lg" fontWeight="bold">
                {actionToConfirm.type === 'deactivate' ? 'Deactivate' : 'Reactivate'} Definition
              </AlertDialogHeader>

              <AlertDialogBody>
                Are you sure you want to {actionToConfirm.type} the custom field definition "{actionToConfirm.label}"?
              </AlertDialogBody>

              <AlertDialogFooter>
                <Button ref={cancelRef} onClick={() => {
                  onAlertClose();
                  setActionToConfirm(null);
                }} isDisabled={isSubmitting}>
                  Cancel
                </Button>
                <Button 
                  colorScheme={actionToConfirm.type === 'deactivate' ? 'red' : 'green'} 
                  onClick={confirmAction} 
                  ml={3}
                  isLoading={isSubmitting}
                >
                  {actionToConfirm.type === 'deactivate' ? 'Deactivate' : 'Reactivate'}
                </Button>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialogOverlay>
        </AlertDialog>
      )}

    </Box>
  );
};

export default CustomFieldDefinitionList;

import React, { useState, useEffect } from 'react';
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  Button,
  VStack,
  Checkbox,
  Text,
  InputGroup,
  InputLeftElement,
  Input,
  Box,
  Divider,
  HStack,
} from '@chakra-ui/react';
import { SearchIcon } from '@chakra-ui/icons';
import type { ColumnDefinition } from './SortableTable'; // Assuming SortableTable exports this

interface ColumnSelectorProps<T> {
  isOpen: boolean;
  onClose: () => void;
  allAvailableColumns: ColumnDefinition<T>[];
  currentVisibleColumnKeys: string[];
  defaultVisibleColumnKeys: string[];
  onApply: (newVisibleColumnKeys: string[]) => void;
  onReset: () => void;
  // tableKey: string; // Not directly needed by this component if callbacks handle it
}

function ColumnSelector<T>({
  isOpen,
  onClose,
  allAvailableColumns,
  currentVisibleColumnKeys,
  defaultVisibleColumnKeys,
  onApply,
  onReset,
}: ColumnSelectorProps<T>) {
  const [selectedKeys, setSelectedKeys] = useState<Set<string>>(new Set(currentVisibleColumnKeys));
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    setSelectedKeys(new Set(currentVisibleColumnKeys));
  }, [currentVisibleColumnKeys, isOpen]); // Reset internal state when currentVisibleKeys change or modal opens

  const handleToggle = (key: string) => {
    setSelectedKeys(prev => {
      const newSet = new Set(prev);
      if (newSet.has(key)) {
        // Prevent unchecking the 'actions' column if it's the only one and meant to be sticky
        // This logic can be enhanced or made configurable if 'actions' is not always special
        if (key === 'actions' && newSet.size === 1) {
            return newSet; // Don't allow unchecking the last item if it's actions
        }
        newSet.delete(key);
      } else {
        newSet.add(key);
      }
      return newSet;
    });
  };

  const handleApply = () => {
    onApply(Array.from(selectedKeys));
    onClose();
  };

  const handleReset = () => {
    setSelectedKeys(new Set(defaultVisibleColumnKeys));
    // Intentionally not calling onApply here, user must explicitly apply reset
    // Or, if direct reset is preferred: 
    // onReset(); 
    // onClose(); 
  };

  const filteredColumns = allAvailableColumns.filter(col => 
    col.header.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Separate standard and custom fields for display, excluding 'actions' from main list if handled separately
  const standardFields = filteredColumns.filter(col => {
    const keyStr = String(col.key);
    return !keyStr.startsWith('cf_') && keyStr !== 'actions';
  });
  const customFields = filteredColumns.filter(col => String(col.key).startsWith('cf_'));
  const actionsField = filteredColumns.find(col => String(col.key) === 'actions');

  return (
    <Modal isOpen={isOpen} onClose={onClose} size="md" scrollBehavior="inside">
      <ModalOverlay />
      <ModalContent>
        <ModalHeader>Select Columns to Display</ModalHeader>
        <ModalCloseButton />
        <ModalBody pb={6}>
          <VStack spacing={4} align="stretch">
            <InputGroup>
              <InputLeftElement pointerEvents="none">
                <SearchIcon color="gray.300" />
              </InputLeftElement>
              <Input 
                placeholder="Search columns..." 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </InputGroup>

            <Box maxHeight="40vh" overflowY="auto" borderWidth="1px" borderRadius="md" p={3}>
              <VStack spacing={3} align="stretch">
                {actionsField && (
                  <Checkbox 
                    key={String(actionsField.key)} 
                    isChecked={selectedKeys.has(String(actionsField.key))} 
                    onChange={() => handleToggle(String(actionsField.key))}
                    isDisabled={selectedKeys.has(String(actionsField.key)) && selectedKeys.size === 1} // Cannot uncheck if it's the only one selected
                  >
                    {actionsField.header}
                  </Checkbox>
                )}
                {actionsField && (standardFields.length > 0 || customFields.length > 0) && <Divider my={2}/>}

                {standardFields.length > 0 && <Text fontWeight="semibold">Standard Fields</Text>}
                {standardFields.map(col => (
                  <Checkbox 
                    key={String(col.key)} 
                    isChecked={selectedKeys.has(String(col.key))} 
                    onChange={() => handleToggle(String(col.key))}
                  >
                    {col.header}
                  </Checkbox>
                ))}

                {standardFields.length > 0 && customFields.length > 0 && <Divider my={2}/>}

                {customFields.length > 0 && <Text fontWeight="semibold">Custom Fields</Text>}
                {customFields.map(col => (
                  <Checkbox 
                    key={String(col.key)} 
                    isChecked={selectedKeys.has(String(col.key))} 
                    onChange={() => handleToggle(String(col.key))}
                  >
                    {col.header}
                  </Checkbox>
                ))}

                {filteredColumns.length === 0 && searchTerm && (
                    <Text textAlign="center" color="gray.500">No columns match "{searchTerm}".</Text>
                )}
              </VStack>
            </Box>
          </VStack>
        </ModalBody>

        <ModalFooter>
          <HStack justifyContent="space-between" width="100%">
            <Button variant="outline" onClick={handleReset} isDisabled={JSON.stringify(Array.from(selectedKeys).sort()) === JSON.stringify(defaultVisibleColumnKeys.sort())}>
              Reset to Defaults
            </Button>
            <Box>
              <Button variant="ghost" mr={3} onClick={onClose}>
                Cancel
              </Button>
              <Button colorScheme="blue" onClick={handleApply}>
                Apply
              </Button>
            </Box>
          </HStack>
        </ModalFooter>
      </ModalContent>
    </Modal>
  );
}

export default ColumnSelector;

import React, { useState, useMemo } from 'react';
import {
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  TableContainer,
} from '@chakra-ui/react';
import { TriangleDownIcon, TriangleUpIcon } from '@chakra-ui/icons';

// Generic Column Definition
export interface ColumnDefinition<T> {
  key: keyof T | string; // Can be a key of T or a custom string for non-direct fields
  header: string;
  renderCell: (item: T) => React.ReactNode;
  isSortable: boolean;
  isNumeric?: boolean;
  sortAccessor?: (item: T) => unknown; // Changed any to unknown
}

// Sort Configuration
interface SortConfig {
  key: string;
  direction: 'ascending' | 'descending';
}

// Component Props
interface SortableTableProps<T extends { id: string }> {
  data: T[];
  columns: ColumnDefinition<T>[];
  initialSortKey: string; // Use string to match ColumnDefinition key type
  initialSortDirection?: 'ascending' | 'descending';
  // Include TableContainer props if needed, e.g., borderWidth, borderRadius
  borderWidth?: string | number;
  borderRadius?: string | number;
}

function SortableTable<T extends { id: string }>({ 
  data, 
  columns, 
  initialSortKey, 
  initialSortDirection = 'ascending',
  borderWidth = "1px", // Default border
  borderRadius = "lg"  // Default radius
}: SortableTableProps<T>) {

  const [sortConfig, setSortConfig] = useState<SortConfig>({ 
      key: initialSortKey,
      direction: initialSortDirection 
  });

  const requestSort = (key: string) => {
    let direction: 'ascending' | 'descending' = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  const sortedData = useMemo(() => {
    const sortableItems = [...data];
    const currentColumn = columns.find(col => col.key === sortConfig.key);

    if (!currentColumn?.isSortable) return sortableItems; // Return unsorted if column not found or not sortable

    sortableItems.sort((a, b) => {
        // Use sortAccessor if provided, otherwise direct key access
        const getSortValue = (item: T) => {
            if (currentColumn.sortAccessor) {
                return currentColumn.sortAccessor(item);
            }
            // Fallback to direct key access using sortConfig.key
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            return (item as any)[sortConfig.key]; 
        };

        let aValue = getSortValue(a);
        let bValue = getSortValue(b);

        // Basic type handling for comparison (can be expanded)
        if (typeof aValue === 'string' && typeof bValue === 'string') {
            aValue = aValue.toLowerCase();
            bValue = bValue.toLowerCase();
        } else if (aValue instanceof Date && bValue instanceof Date) {
             aValue = aValue.getTime();
             bValue = bValue.getTime();
        } else if (typeof aValue === 'boolean' && typeof bValue === 'boolean') {
             aValue = aValue ? 1 : 0;
             bValue = bValue ? 1 : 0;
        }

        // Handle null/undefined consistently (e.g., sort to end when ascending)
        const isAscending = sortConfig.direction === 'ascending';
        if (aValue == null && bValue != null) return isAscending ? 1 : -1;
        if (aValue != null && bValue == null) return isAscending ? -1 : 1;
        if (aValue == null && bValue == null) return 0;

        // Standard comparison
        if (aValue < bValue) return isAscending ? -1 : 1;
        if (aValue > bValue) return isAscending ? 1 : -1;
        return 0;
    });
    return sortableItems;
  }, [data, columns, sortConfig]);

  const renderSortIcon = (columnKey: string) => {
      if (sortConfig.key !== columnKey) return null;
      return sortConfig.direction === 'ascending' ? 
             <TriangleUpIcon aria-label="sorted ascending" ml={1} w={3} h={3} /> : 
             <TriangleDownIcon aria-label="sorted descending" ml={1} w={3} h={3} />;
  };

  return (
    <TableContainer width="100%" borderWidth={borderWidth} borderRadius={borderRadius}>
      <Table variant="simple" size="sm" width="100%">
        <Thead>
          <Tr borderBottomWidth="1px" borderColor="gray.200">
            {columns.map((column) => (
              <Th
                key={String(column.key)} // Ensure key is string
                isNumeric={column.isNumeric}
                cursor={column.isSortable ? "pointer" : "default"}
                _hover={column.isSortable ? { bg: 'gray.100' } : {}}
                onClick={column.isSortable ? () => requestSort(String(column.key)) : undefined}
              >
                {column.header}
                {column.isSortable && renderSortIcon(String(column.key))}
              </Th>
            ))}
          </Tr>
        </Thead>
        <Tbody>
          {sortedData.map((item) => (
            <Tr key={item.id} bg="white">
              {columns.map((column) => (
                <Td key={`${item.id}-${String(column.key)}`} isNumeric={column.isNumeric}>
                  {column.renderCell(item)}
                </Td>
              ))}
            </Tr>
          ))}
        </Tbody>
      </Table>
    </TableContainer>
  );
}

export default SortableTable;

import React from 'react';
import { useThemeStore, ThemeMode } from '../../stores/useThemeStore';
import { IconButton, useColorMode, Menu, MenuButton, MenuList, MenuItem, Text } from '@chakra-ui/react';
import { SunIcon, MoonIcon, ChevronDownIcon, StarIcon } from '@chakra-ui/icons';

const ThemeSwitcher: React.FC = () => {
  const currentTheme = useThemeStore((state) => state.currentTheme);
  const setCurrentTheme = useThemeStore((state) => state.setCurrentTheme);
  const { setColorMode } = useColorMode();

  // Chakra's useColorMode can still be useful for components that inherently use it,
  // but our primary theme switching is now manual via Zustand.
  // We might not need toggleColorMode directly if our themes explicitly set colors.
  // const { colorMode, toggleColorMode } = useColorMode(); 

  const handleThemeChange = (newTheme: ThemeMode) => {
    setCurrentTheme(newTheme);
    // For Chakra's internal mode, Ocean Breeze, Bowie & Industrial are considered 'light' or 'dark'
    // based on their overall brightness. Bowie & Industrial are dark-based.
    let chakraColorMode: 'light' | 'dark';
    if (newTheme === 'dark' || newTheme === 'bowie' || newTheme === 'industrialMetal' || newTheme === 'daliDark') {
      chakraColorMode = 'dark';
    } else {
      chakraColorMode = 'light';
    }
    setColorMode(chakraColorMode);
  };

  const getThemeIcon = (theme: ThemeMode) => {
    if (theme === 'light') return <SunIcon />;
    if (theme === 'dark') return <MoonIcon />;
    if (theme === 'daliDark') return <Text as="span"></Text>; // Dali Eye Icon
    if (theme === 'bowie') return <Text as="span"></Text>;
    if (theme === 'industrialMetal') return <Text as="span"></Text>;
    if (theme === 'andyWarhol') return <Text as="span"></Text>;
    return <ChevronDownIcon />; // Fallback for button
  };

  const themeLabels: Record<ThemeMode, string> = {
    light: 'Light Mode',
    dark: 'Dark Mode',
    daliDark: 'Dark Salvador Dali', // Renamed and relabeled
    bowie: 'David Bowie',
    industrialMetal: 'Industrial Metal',
    andyWarhol: 'Andy Warhol',
  };

  return (
    <Menu>
      <MenuButton
        as={IconButton}
        aria-label="Select theme"
        icon={getThemeIcon(currentTheme)}
        variant="ghost"
      />
      <MenuList>
        {(Object.keys(themeLabels) as ThemeMode[]).map((themeKey) => (
          <MenuItem
            key={themeKey}
            icon={getThemeIcon(themeKey)}
            onClick={() => handleThemeChange(themeKey)}
            fontWeight={currentTheme === themeKey ? 'bold' : 'normal'}
          >
            {themeLabels[themeKey]}
          </MenuItem>
        ))}
      </MenuList>
    </Menu>
  );
};

export default ThemeSwitcher;

import React from 'react';
import { Box, Text, VStack, Heading, Tooltip, useColorModeValue, useTheme } from '@chakra-ui/react';
import { Deal } from '../../stores/useDealsStore';
import { Draggable, DraggableProvided, DraggableStateSnapshot } from '@hello-pangea/dnd'; // Import Draggable & its types
import { Link as RouterLink } from 'react-router-dom'; // Import Link
import { useThemeStore } from '../../stores/useThemeStore'; // Import theme store

interface DealCardKanbanProps {
  deal: Deal;
  index: number; // Required by react-beautiful-dnd for Draggable
}

const DealCardKanban: React.FC<DealCardKanbanProps> = ({ deal, index }) => {
  const { currentTheme } = useThemeStore();
  const theme = useTheme();

  // Function to calculate effective probability for display (can be expanded)
  const getEffectiveProbabilityDisplay = () => {
    let probability = deal.deal_specific_probability;
    let source = 'deal';
    if (probability == null && deal.stage?.deal_probability != null) {
      probability = deal.stage.deal_probability;
      source = 'stage';
    }
    if (probability == null) return 'N/A';
    return `${Math.round(probability * 100)}% (${source})`;
  };

  // Default Theme-aware colors
  const defaultCardBgBase = useColorModeValue('white', 'gray.800');
  const defaultCardBgDragging = useColorModeValue('green.50', 'green.800');
  const defaultCardBorderColor = useColorModeValue('gray.200', 'gray.600');
  const defaultAmountTextColor = useColorModeValue('gray.700', 'gray.200');
  const defaultSecondaryTextColor = useColorModeValue('gray.500', 'gray.400');
  const defaultProbabilityTextColor = useColorModeValue('purple.600', 'purple.300');

  // Warhol-specific dark theme colors
  const warholCardBgBase = theme.colors.gray[900]; // black
  const warholCardBgDragging = theme.colors.blue[800]; // dark popBlue
  const warholCardBorderColor = theme.colors.yellow[500]; // popYellow
  const warholAmountTextColor = theme.colors.gray[50];
  const warholSecondaryTextColor = theme.colors.gray[100];
  const warholProbabilityTextColor = theme.colors.green[500]; // popGreen
  const warholHeadingColor = theme.colors.gray[50];

  const cardBgBase = currentTheme === 'andyWarhol' ? warholCardBgBase : defaultCardBgBase;
  const cardBgDragging = currentTheme === 'andyWarhol' ? warholCardBgDragging : defaultCardBgDragging;
  const cardBorderColor = currentTheme === 'andyWarhol' ? warholCardBorderColor : defaultCardBorderColor;
  const amountTextColor = currentTheme === 'andyWarhol' ? warholAmountTextColor : defaultAmountTextColor;
  const secondaryTextColor = currentTheme === 'andyWarhol' ? warholSecondaryTextColor : defaultSecondaryTextColor;
  const probabilityTextColor = currentTheme === 'andyWarhol' ? warholProbabilityTextColor : defaultProbabilityTextColor;
  const headingColor = currentTheme === 'andyWarhol' ? warholHeadingColor : useColorModeValue(theme.colors.gray[900], theme.colors.gray[50]);

  return (
    <Draggable draggableId={deal.id} index={index}>
      {(provided: DraggableProvided, snapshot: DraggableStateSnapshot) => ( // Add types to provided and snapshot
        <Box
          ref={provided.innerRef} // Connect ref
          {...provided.draggableProps} // Spread draggable props
          {...provided.dragHandleProps} // Spread drag handle props
          p={3}
          bg={snapshot.isDragging ? cardBgDragging : cardBgBase} // Change bg based on dragging state
          borderRadius="md"
          boxShadow={snapshot.isDragging ? "xl" : "sm"} // Enhance shadow when dragging
          borderWidth="1px"
          borderColor={cardBorderColor}
          style={{ 
            ...provided.draggableProps.style, // Important for D&D positioning
            // userSelect: "none", // Prevent text selection during drag (optional)
          }}
          mb={3} // Margin between cards
        >
          <VStack align="stretch" spacing={1}>
            <Tooltip label={deal.name} placement="top" openDelay={500}>
                <RouterLink to={`/deals/${deal.id}`} style={{ textDecoration: 'none', display: 'block', color: 'inherit' }}>
                    <Heading size="xs" isTruncated _hover={{ textDecoration: 'underline' }} color={headingColor}>{deal.name}</Heading>
                </RouterLink>
            </Tooltip>
            <Text fontSize="sm" color={amountTextColor}>
              {deal.amount ? 
                new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(deal.amount) 
                : 'No amount'}
            </Text>
            {deal.person && (
              <Text fontSize="xs" color={secondaryTextColor} isTruncated>
                Person: {deal.person.first_name || ''} {deal.person.last_name || ''}
              </Text>
            )}
            {deal.organization && (
              <Text fontSize="xs" color={secondaryTextColor} isTruncated>
                Org: {deal.organization.name}
              </Text>
            )}
            <Text fontSize="xs" color={probabilityTextColor}>
                Prob: {getEffectiveProbabilityDisplay()}
            </Text>
            {/* Add more compact deal info as needed */}
          </VStack>
        </Box>
      )}
    </Draggable>
  );
};

export default DealCardKanban;

import { Box, Text, VStack, HStack, Avatar, UnorderedList, ListItem, chakra } from '@chakra-ui/react';
import { format, parseISO } from 'date-fns';
import { DealHistoryEntryDisplayItem } from './DealHistoryList'; // Import the type

interface DealHistoryItemProps {
  entry: DealHistoryEntryDisplayItem;
}

const fieldDisplayNames: Record<string, string> = {
  name: 'Name',
  stage_id: 'Stage',
  amount: 'Amount',
  expected_close_date: 'Expected Close Date',
  person_id: 'Person',
  organization_id: 'Organization',
  deal_specific_probability: 'Deal Specific Probability',
};

const formatFieldValue = (field: string, value: any): string => {
  if (value === null || value === undefined) return 'N/A';
  switch (field) {
    case 'amount':
      return `$${Number(value).toLocaleString()}`;
    case 'deal_specific_probability':
      return `${Number(value) * 100}%`;
    case 'expected_close_date':
      try {
        return format(parseISO(value), 'MMM d, yyyy'); // Assuming ISO string
      } catch (e) {
        return String(value); // Fallback if not a valid date string
      }
    case 'stage_id':
    case 'person_id':
    case 'organization_id':
      return `${value}`; // Display ID, add (ID) for clarity if needed, or resolve name later
    default:
      return String(value);
  }
};

// Helper function to render changes
const renderChanges = (eventType: string, changes: any): JSX.Element | string => {
  if (eventType === 'DEAL_DELETED') {
    return 'This deal was deleted.';
  }
  if (!changes || Object.keys(changes).length === 0) {
    return 'No specific changes logged.';
  }

  if (eventType === 'DEAL_CREATED') {
    return (
      <UnorderedList spacing={1} styleType="none" ml={0}>
        {Object.entries(changes).map(([key, value]) => (
          <ListItem key={key}>
            <Text as="span" fontWeight="medium">{fieldDisplayNames[key] || key}:</Text> {formatFieldValue(key, value)}
          </ListItem>
        ))}
      </UnorderedList>
    );
  }

  if (eventType === 'DEAL_UPDATED') {
    return (
      <UnorderedList spacing={1} styleType="none" ml={0}>
        {Object.entries(changes).map(([key, value]: [string, any]) => (
          <ListItem key={key}>
            <Text as="span" fontWeight="medium">{fieldDisplayNames[key] || key}:</Text> 
            changed from "{formatFieldValue(key, value.oldValue)}" to "{formatFieldValue(key, value.newValue)}"
          </ListItem>
        ))}
      </UnorderedList>
    );
  }
  // Fallback for other event types or if structure is unexpected
  return <chakra.pre fontSize="xs" fontFamily="mono">{JSON.stringify(changes, null, 2)}</chakra.pre>;
};

const DealHistoryItem: React.FC<DealHistoryItemProps> = ({ entry }) => {
  const userName = entry.user?.display_name || 'System Action';
  // Placeholder for avatar, replace with actual avatar URL if available
  const userAvatarSrc = entry.user?.display_name ? `https://ui-avatars.com/api/?name=${encodeURIComponent(entry.user.display_name)}&background=random&color=fff` : undefined;

  let eventDescription = 'made changes to this deal.';
  if (entry.eventType === 'DEAL_CREATED') eventDescription = 'created this deal.';
  if (entry.eventType === 'DEAL_UPDATED') eventDescription = 'updated this deal.';
  if (entry.eventType === 'DEAL_DELETED') eventDescription = 'deleted this deal.';

  return (
    <Box 
      borderWidth="1px" 
      borderRadius="md" 
      p={4} 
      bg={{ base: 'white', _dark: 'gray.700' }} 
      borderColor={{ base: 'gray.200', _dark: 'gray.600' }} 
      shadow="sm"
    >
      <HStack spacing={3} align="start">
        <Avatar size="sm" name={userName} src={userAvatarSrc} mt={1} />
        <VStack align="start" spacing={1} flex={1}>
          <HStack justifyContent="space-between" w="full">
            <Text fontWeight="bold" fontSize="sm">{userName}</Text>
            <Text fontSize="xs" color={{ base: 'gray.500', _dark: 'gray.400' }}>
              {format(parseISO(entry.createdAt), 'MMM d, yyyy, h:mm a')} 
            </Text>
          </HStack>
          <Text fontSize="sm" color={{ base: 'gray.700', _dark: 'gray.200' }}>
            {eventDescription}
          </Text>
          {entry.changes && (
            <Box pt={1} fontSize="sm">
