import { useEffect, useState } from 'react';
import { Box, VStack, FormControl, FormLabel, Input, Button, useToast, FormErrorMessage } from '@chakra-ui/react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { gql } from 'graphql-request';
import { gqlClient } from '../../lib/graphqlClient';
import { isGraphQLErrorWithMessage } from '../../lib/graphqlUtils';
import type { User, UpdateUserProfileInput } from '../../generated/graphql/graphql';

interface ProfileEditFormProps {
  user: User;
  onUpdateSuccess: (updatedUser: User) => void;
  onCancel?: () => void;
}

interface FormValues {
  display_name: string;
  avatar_url: string;
}

// Define the mutation string directly for graphql-request
const UPDATE_USER_PROFILE_MUTATION = gql`
  mutation UpdateUserProfile($input: UpdateUserProfileInput!) {
    updateUserProfile(input: $input) {
      id
      email
      display_name
      avatar_url
    }
  }
`;

const ProfileEditForm: React.FC<ProfileEditFormProps> = ({ user, onUpdateSuccess, onCancel }) => {
  console.log('[ProfileEditForm] Rendering, current user:', user);
  const toast = useToast();
  const [mutationLoading, setMutationLoading] = useState(false);
  const {
    handleSubmit,
    register,
    reset,
    formState: { errors, isSubmitting },
  } = useForm<FormValues>({
    defaultValues: {
      display_name: user.display_name || '',
      avatar_url: user.avatar_url || '',
    },
  });

  console.log('[ProfileEditForm] RHF errors object:', errors);

  useEffect(() => {
    reset({
      display_name: user.display_name || '',
      avatar_url: user.avatar_url || '',
    });
  }, [user, reset]);

  const onSubmit: SubmitHandler<FormValues> = async (values) => {
    const input: UpdateUserProfileInput = {};
    // Only include fields if they have changed or are being set
    // For simplicity, we send them if they are different or if they were empty and now have value.
    // More robust: check against initial values if truly only sending deltas is critical.
    if (values.display_name !== (user.display_name || '')) {
      input.display_name = values.display_name.trim() === '' ? null : values.display_name.trim();
    }
    if (values.avatar_url !== (user.avatar_url || '')) {
      input.avatar_url = values.avatar_url.trim() === '' ? null : values.avatar_url.trim();
    }

    // If no actual changes to submit (e.g., user made fields empty that were already null)
    // This can be refined to check if input object is empty if submitting nulls is expensive
    if (Object.keys(input).length === 0 && 
        (values.display_name.trim() === (user.display_name || '')) && 
        (values.avatar_url.trim() === (user.avatar_url || ''))) {
      toast({
        title: 'No Changes',
        description: 'No changes were made to your profile.',
        status: 'info',
        duration: 3000,
        isClosable: true,
      });
      if (onCancel) onCancel(); // Optionally close form if no changes
      return;
    }

    setMutationLoading(true);
    try {
      // Replace useMutation call with gqlClient.request
      const { updateUserProfile: updatedUser } = await gqlClient.request<
        { updateUserProfile: User },
        { input: UpdateUserProfileInput }
      >(UPDATE_USER_PROFILE_MUTATION, { input });

      if (updatedUser) {
        toast({
          title: 'Profile Updated',
          description: 'Your profile has been successfully updated.',
          status: 'success',
          duration: 5000,
          isClosable: true,
        });
        onUpdateSuccess(updatedUser);
      } else {
        throw new Error('Failed to update profile: No data returned');
      }
    } catch (error: any) { // Type error as any for broader catch
      console.error('Error updating profile:', error);
      let errorMessage = 'Could not update your profile.';
      if (isGraphQLErrorWithMessage(error)) {
        errorMessage = error.response!.errors[0].message || errorMessage;
      } else if (error instanceof Error) {
        errorMessage = error.message;
      }
      toast({
        title: 'Update Failed',
        description: errorMessage,
        status: 'error',
        duration: 9000,
        isClosable: true,
      });
    } finally {
      setMutationLoading(false);
    }
  };

  return (
    <Box as="form" onSubmit={handleSubmit(onSubmit)} width="100%">
      <VStack spacing={4}>
        <FormControl isInvalid={!!errors.display_name}>
          <FormLabel htmlFor="display_name">Display Name</FormLabel>
          <Input
            id="display_name"
            {...register('display_name')}
          />
          <FormErrorMessage>
            {errors.display_name && errors.display_name.message}
          </FormErrorMessage>
        </FormControl>

        <FormControl isInvalid={!!errors.avatar_url}>
          <FormLabel htmlFor="avatar_url">Avatar URL</FormLabel>
          <Input
            id="avatar_url"
            type="url"
            {...register('avatar_url', {
              pattern: {
                value: /^(ftp|http|https):\/\/[^ "]+$/,
                message: 'Please enter a valid URL.',
              },
            })}
          />
          <FormErrorMessage>
            {errors.avatar_url && errors.avatar_url.message}
          </FormErrorMessage>
        </FormControl>

        <Button 
          type="submit" 
          colorScheme="blue" 
          isLoading={isSubmitting || mutationLoading}
          width="full"
        >
          Save Changes
        </Button>
        {onCancel && (
          <Button variant="ghost" onClick={onCancel} width="full">
            Cancel
          </Button>
        )}
      </VStack>
    </Box>
  );
};

export default ProfileEditForm;

import React, { useState, useEffect } from 'react';
import { 
  Modal, 
  ModalOverlay, 
  ModalContent, 
  ModalHeader, 
  ModalFooter, 
  ModalBody, 
  ModalCloseButton, 
  Button, 
  FormControl, 
  FormLabel, 
  Input, 
  NumberInput, // Use NumberInput for order and probability
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
  Select, // Added Select
  useToast, 
  VStack 
} from '@chakra-ui/react';
import { useStagesStore } from '../../stores/useStagesStore';
import type { CreateStageInput as GeneratedCreateStageInput, StageType } from '../../generated/graphql/graphql';
import { StageType as StageTypeEnum } from '../../generated/graphql/graphql';

interface CreateStageModalProps {
  isOpen: boolean;
  onClose: () => void;
  pipelineId: string; // Required to link the stage
  // Optional: Callback on successful creation
  onSuccess?: (newStageId: string) => void; 
}

const CreateStageModal: React.FC<CreateStageModalProps> = ({ isOpen, onClose, pipelineId, onSuccess }) => {
  const [stageName, setStageName] = useState('');
  const [stageOrder, setStageOrder] = useState<number | string>(0); // Store as number or string for NumberInput
  const [dealProbability, setDealProbability] = useState<number | string>(''); // Optional, use empty string for placeholder
  const [stageType, setStageType] = useState<StageType>(StageTypeEnum.Open); // Added stageType state, ensure it uses the enum
  const [isDealProbabilityDisabled, setIsDealProbabilityDisabled] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { createStage, stagesError } = useStagesStore();
  const toast = useToast();

  // Reset form when modal is opened/closed
  useEffect(() => {
    if (!isOpen) {
        setStageName('');
        setStageOrder(0); // Reset to default
        setDealProbability('');
        setStageType(StageTypeEnum.Open); // Reset stageType
        setIsDealProbabilityDisabled(false); // Reset disabled state
        setIsLoading(false);
    }
  }, [isOpen]);

  // New useEffect to handle deal probability based on stageType
  useEffect(() => {
    if (stageType === StageTypeEnum.Won) {
      setDealProbability(100);
      setIsDealProbabilityDisabled(true);
    } else if (stageType === StageTypeEnum.Lost) {
      setDealProbability(0);
      setIsDealProbabilityDisabled(true);
    } else { // StageTypeEnum.Open or any other
      setIsDealProbabilityDisabled(false);
      // Optionally, you could reset dealProbability to '' if coming from WON/LOST
      // if it was previously auto-set, or leave it as is for user to modify.
      // For now, just enabling is fine.
    }
  }, [stageType]);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    // Validation
    if (!stageName.trim()) {
      toast({ title: "Stage name cannot be empty.", status: 'warning', duration: 3000, isClosable: true });
      return;
    }
    const orderNum = parseInt(String(stageOrder), 10);
    if (isNaN(orderNum) || orderNum < 0) {
         toast({ title: "Order must be a non-negative number.", status: 'warning', duration: 3000, isClosable: true });
      return;
    }

    // Probability validation starts here, moved outside the orderNum validation block
    let probabilityNum: number | null = null;
    if (String(dealProbability).trim() !== '') {
        const parsedProb = parseFloat(String(dealProbability)); // Use temporary variable
        if (isNaN(parsedProb) || parsedProb < 0 || parsedProb > 100) { 
            toast({ title: "Deal probability must be between 0 and 100 (or empty).", status: 'warning', duration: 3000, isClosable: true });
            return;
        }
        probabilityNum = parsedProb; // Assign if valid
    }

    setIsLoading(true);
    try {
        const input: GeneratedCreateStageInput = {
            pipeline_id: pipelineId,
            name: stageName.trim(),
            order: orderNum,
            // Convert percentage (0-100 or null) to decimal (0-1 or null)
            deal_probability: probabilityNum === null ? null : probabilityNum / 100, 
            stage_type: stageType, // Added stage_type to input
        };

      const newStage = await createStage(input);

      if (newStage) {
        toast({ title: "Stage created successfully.", status: 'success', duration: 3000, isClosable: true });
        onSuccess?.(newStage.id);
        onClose(); // Close modal
      } else {
        toast({ title: "Failed to create stage.", description: stagesError || "Please check console or try again.", status: 'error', duration: 5000, isClosable: true });
      }
    } catch (error: unknown) {
        console.error("Error in create stage modal submit:", error);
        let message = "Could not create stage.";
        if (error instanceof Error) {
            message = error.message;
        } else if (typeof error === 'string') {
            message = error;
        }
        toast({ title: "An error occurred.", description: message, status: 'error', duration: 5000, isClosable: true });
    } finally {
        setIsLoading(false);
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} isCentered>
      <ModalOverlay />
      <ModalContent as="form" onSubmit={handleSubmit}>
        <ModalHeader>Create New Stage</ModalHeader>
        <ModalCloseButton />
        <ModalBody>
          <VStack spacing={4}>
            <FormControl isRequired>
              <FormLabel>Stage Name</FormLabel>
              <Input 
                placeholder="e.g., Qualification"
                value={stageName}
                onChange={(e) => setStageName(e.target.value)}
                autoFocus // eslint-disable-line jsx-a11y/no-autofocus
              />
            </FormControl>

            <FormControl isRequired>
                <FormLabel>Order</FormLabel>
                <NumberInput 
                    min={0} 
                    value={stageOrder} 
                    onChange={(valueAsString, valueAsNumber) => setStageOrder(isNaN(valueAsNumber) ? valueAsString : valueAsNumber)} 
                >
                    <NumberInputField />
                    <NumberInputStepper>
                        <NumberIncrementStepper />
                        <NumberDecrementStepper />
                    </NumberInputStepper>
