import React, { useEffect, useState, useMemo } from 'react';
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
  useToast,
  HStack,
  IconButton,
  Spinner,
  Box,
  Button
} from '@chakra-ui/react';
import { AddIcon, EditIcon, DeleteIcon, SettingsIcon } from '@chakra-ui/icons';
import { usePeopleStore, Person } from '../stores/usePeopleStore';
import { useAppStore } from '../stores/useAppStore';
import { useViewPreferencesStore } from '../stores/useViewPreferencesStore';
import type { CustomFieldDefinition, CustomFieldValue, CustomFieldEntityType, CustomFieldType as GQLCustomFieldType } from '../generated/graphql/graphql';
import { gqlClient } from '../lib/graphqlClient';
import { gql } from 'graphql-request';

import CreatePersonForm from '../components/CreatePersonForm';
import EditPersonForm from '../components/EditPersonForm';
import ConfirmationDialog from '../components/common/ConfirmationDialog';
import ListPageLayout from '../components/layout/ListPageLayout';
import SortableTable, { ColumnDefinition } from '../components/common/SortableTable';
import ColumnSelector from '../components/common/ColumnSelector';
import QuickFilterControls, { QuickFilter } from '../components/common/QuickFilterControls';

const isUrl = (str: string): boolean => {
  try {
    new URL(str);
    return str.startsWith('http://') || str.startsWith('https://');
  } catch (_) {
    return false;
  }
};

const GET_PERSON_CUSTOM_FIELD_DEFS_QUERY = gql`
  query GetPersonCustomFieldDefinitions {
    customFieldDefinitions(entityType: PERSON, includeInactive: false) {
      id
      fieldName
      fieldLabel
      fieldType
      dropdownOptions { value label }
    }
  }
`;

function PeoplePage() {
  const { people, peopleLoading, peopleError, fetchPeople, deletePerson } = usePeopleStore();
  const { userPermissions } = useAppStore();
  const { 
    tableColumnPreferences, 
    initializeTable, 
    setVisibleColumnKeys,
    resetTableToDefaults
  } = useViewPreferencesStore();

  const { isOpen: isCreateOpen, onOpen: onCreateOpen, onClose: onCreateClose } = useDisclosure();
  const [personToEdit, setPersonToEdit] = useState<Person | null>(null);
  const { isOpen: isEditOpen, onOpen: onEditOpen, onClose: onEditClose } = useDisclosure();
  const { isOpen: isConfirmDeleteDialogOpen, onOpen: onConfirmDeleteOpen, onClose: onConfirmDeleteClose } = useDisclosure();
  const [personIdToDelete, setPersonIdToDelete] = useState<string | null>(null);
  const toast = useToast();

  const [personCustomFieldDefinitions, setPersonCustomFieldDefinitions] = useState<CustomFieldDefinition[]>([]);
  const [customFieldsLoading, setCustomFieldsLoading] = useState<boolean>(true);
  const { isOpen: isColumnSelectorOpen, onOpen: onColumnSelectorOpen, onClose: onColumnSelectorClose } = useDisclosure();

  const [activeQuickFilterKey, setActiveQuickFilterKey] = useState<string | null>(null);

  // Define Quick Filters for People
  const availableQuickFilters = useMemo((): QuickFilter[] => [
    { key: 'all', label: 'All People' },
    { key: 'noOrg', label: 'Without Organization' },
    { key: 'withEmail', label: 'With Email' },
    { key: 'noPhone', label: 'Without Phone' },
  ], []);

  useEffect(() => {
    fetchPeople();
  }, [fetchPeople]);

  useEffect(() => {
    const fetchCustomFieldDefs = async () => {
      setCustomFieldsLoading(true);
      try {
        const data = await gqlClient.request<{ customFieldDefinitions: CustomFieldDefinition[] }>(GET_PERSON_CUSTOM_FIELD_DEFS_QUERY);
        setPersonCustomFieldDefinitions(data.customFieldDefinitions || []);
      } catch (error) {
        console.error("Error fetching person custom field definitions:", error);
        toast({ title: 'Error loading custom field definitions', status: 'error', duration: 3000 });
      } finally {
        setCustomFieldsLoading(false);
      }
    };
    fetchCustomFieldDefs();
  }, [toast]);

  const TABLE_KEY = 'people_list';

  const allAvailableColumns = useMemo((): ColumnDefinition<Person>[] => {
    const standardColumns: ColumnDefinition<Person>[] = [
      { 
        key: 'name', 
        header: 'Name', 
        renderCell: (person) => `${person.first_name || ''} ${person.last_name || ''}`.trim() || '-', 
        isSortable: true, 
        sortAccessor: (p) => `${p.first_name || ''} ${p.last_name || ''}`.toLowerCase().trim()
      },
      { 
        key: 'email', 
        header: 'Email', 
        renderCell: (person) => person.email || '-', 
        isSortable: true, 
        sortAccessor: (p) => p.email?.toLowerCase() 
      },
      { 
        key: 'phone', 
        header: 'Phone', 
        renderCell: (person) => person.phone || '-', 
        isSortable: false 
      },
      { 
        key: 'organization', 
        header: 'Organization', 
        renderCell: (person) => person.organization?.name || '-', 
        isSortable: true, 
        sortAccessor: (p) => p.organization?.name?.toLowerCase() 
      },
      {
        key: 'created_at',
        header: 'Created At',
        renderCell: (person) => person.created_at ? new Date(person.created_at).toLocaleDateString() : '-',
        isSortable: true,
        sortAccessor: (p) => p.created_at ? new Date(p.created_at).getTime() : 0,
      },
    ];

    const customFieldColumns: ColumnDefinition<Person>[] = personCustomFieldDefinitions.map(def => ({
      key: `cf_${def.fieldName}`,
      header: def.fieldLabel,
      isSortable: true,
      sortAccessor: (person: Person) => {
        const cfValue = person.customFieldValues?.find(cf => cf.definition.fieldName === def.fieldName);
        if (!cfValue) return '';
        switch (def.fieldType) {
          case 'TEXT': return cfValue.stringValue?.toLowerCase() || '';
          case 'NUMBER': return cfValue.numberValue || 0;
          case 'DATE': return cfValue.dateValue ? new Date(cfValue.dateValue).getTime() : 0;
          case 'BOOLEAN': return cfValue.booleanValue ? 'true' : 'false';
          default: return cfValue.stringValue?.toLowerCase() || '';
        }
      },
      renderCell: (person: Person) => {
        const cfValue = person.customFieldValues?.find(cf => cf.definition.fieldName === def.fieldName);
        if (!cfValue) return '-';

        let displayValue: React.ReactNode = '-';
        switch (def.fieldType) {
          case 'TEXT':
            displayValue = cfValue.stringValue || '-';
            if (cfValue.stringValue && isUrl(cfValue.stringValue)) {
              displayValue = <a href={cfValue.stringValue} target="_blank" rel="noopener noreferrer" style={{color: 'blue.500', textDecoration: 'underline'}}>{cfValue.stringValue}</a>;
            }
            break;
          case 'NUMBER':
            displayValue = cfValue.numberValue?.toString() ?? '-';
            break;
          case 'DATE':
            displayValue = cfValue.dateValue ? new Date(cfValue.dateValue).toLocaleDateString() : '-';
            break;
          case 'BOOLEAN':
            displayValue = cfValue.booleanValue ? 'Yes' : 'No';
            break;
          case 'DROPDOWN':
            const selectedOptVal = cfValue.stringValue;
            const selectedOpt = def.dropdownOptions?.find(opt => opt.value === selectedOptVal);
            displayValue = selectedOpt?.label || selectedOptVal || '-';
            break;
          case 'MULTI_SELECT':
            const selectedVals = cfValue.selectedOptionValues || [];
            displayValue = selectedVals.map(val => def.dropdownOptions?.find(opt => opt.value === val)?.label || val).join(', ') || '-';
            break;
          default:
            displayValue = cfValue.stringValue || '-';
        }
        return displayValue;
      },
    }));

    const actionsColumn: ColumnDefinition<Person> = {
      key: 'actions',
      header: 'Actions',
      renderCell: (person) => (
        <HStack spacing={2}>
          <IconButton 
            icon={<EditIcon />} 
            aria-label="Edit person" 
            size="sm" 
            onClick={() => handleEditClick(person)} 
            isDisabled={!userPermissions?.includes('person:update_any') && !userPermissions?.includes('person:update_own')} 
          />
          <IconButton 
            icon={<DeleteIcon />} 
            aria-label="Delete person" 
            size="sm" 
            variant="ghost"
            colorScheme="red" 
            onClick={() => handleDeleteClick(person.id)} 
            isDisabled={!userPermissions?.includes('person:delete_any') && !userPermissions?.includes('person:delete_own')} 
          />
        </HStack>
      ),
      isSortable: false,
    };

    return [...standardColumns, ...customFieldColumns, actionsColumn];
  }, [personCustomFieldDefinitions, userPermissions]);

  const defaultVisibleColumnKeys = useMemo(() => [
    'name', 
    'email', 
    'phone', 
    'organization',
    'actions'
  ], []);

  useEffect(() => {
    if (allAvailableColumns.length > 0) {
        initializeTable(TABLE_KEY, defaultVisibleColumnKeys);
    }
  }, [initializeTable, defaultVisibleColumnKeys, allAvailableColumns]);

  const currentVisibleColumnKeys = tableColumnPreferences[TABLE_KEY]?.visibleColumnKeys || defaultVisibleColumnKeys;

  const visibleColumns = useMemo(() => {
    if (customFieldsLoading || allAvailableColumns.length === 0) return [];
    return allAvailableColumns.filter(col => currentVisibleColumnKeys.includes(String(col.key)));
  }, [allAvailableColumns, currentVisibleColumnKeys, customFieldsLoading]);

  const handleEditClick = (person: Person) => {
    setPersonToEdit(person);
    onEditOpen();
  };

  const handleDeleteClick = (personId: string) => {
    setPersonIdToDelete(personId);
    onConfirmDeleteOpen();
  };

  const handleConfirmDelete = async () => {
    if (personIdToDelete) {
      const success = await deletePerson(personIdToDelete);
      if (success) {
        toast({ title: 'Person deleted', status: 'success', duration: 3000, isClosable: true });
      } else {
        toast({ title: 'Error', description: peopleError || 'Failed to delete person.', status: 'error', duration: 3000, isClosable: true });
      }
      setPersonIdToDelete(null);
      onConfirmDeleteClose();
    }
  };

  const displayedPeople = useMemo(() => {
    if (!activeQuickFilterKey || activeQuickFilterKey === 'all') {
      return people;
    }
    return people.filter(person => {
      switch (activeQuickFilterKey) {
        case 'noOrg':
          return !person.organization_id && !person.organization;
        case 'withEmail':
          return !!person.email;
        case 'noPhone':
          return !person.phone;
        default:
          return true;
      }
    });
  }, [people, activeQuickFilterKey]);

  const pageIsLoading = peopleLoading || customFieldsLoading;

  return (
    <>
    <ListPageLayout
      title="People"
      newButtonLabel="New Person"
      onNewButtonClick={onCreateOpen}
      isNewButtonDisabled={!userPermissions?.includes('person:create')}
      isLoading={pageIsLoading}
      error={peopleError}
      isEmpty={!pageIsLoading && displayedPeople.length === 0 && !peopleError}
      emptyStateProps={{
        icon: AddIcon,
        title: "No People Found",
        message: "Get started by adding your first person or try a different filter.",
      }}
      customControls={
        <HStack spacing={4} my={2}>
          <QuickFilterControls
            availableFilters={availableQuickFilters}
            activeFilterKey={activeQuickFilterKey}
            onSelectFilter={setActiveQuickFilterKey}
          />
          <Button leftIcon={<SettingsIcon />} onClick={onColumnSelectorOpen} size="sm" variant="outline">
            Columns
          </Button>
        </HStack>
      }
    >
      {!pageIsLoading && !peopleError && displayedPeople.length > 0 && (
        <SortableTable<Person>
          data={displayedPeople}
          columns={visibleColumns}
          initialSortKey="name"
          initialSortDirection="ascending"
        />
      )}
      {(pageIsLoading || customFieldsLoading) && <Spinner />}
    </ListPageLayout>

    {isCreateOpen && (
      <Modal isOpen={isCreateOpen} onClose={onCreateClose} size="xl" isCentered scrollBehavior="inside">
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Create New Person</ModalHeader>
          <ModalCloseButton />
          <ModalBody pb={6}>
            <CreatePersonForm onSuccess={onCreateClose} onClose={onCreateClose} />
          </ModalBody>
        </ModalContent>
      </Modal>
    )}

    {isEditOpen && personToEdit && (
      <Modal isOpen={isEditOpen} onClose={onEditClose} size="xl" isCentered scrollBehavior="inside">
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Edit Person</ModalHeader>
          <ModalCloseButton />
          <ModalBody pb={6}>
            <EditPersonForm person={personToEdit} onSuccess={onEditClose} onClose={onEditClose} />
          </ModalBody>
        </ModalContent>
      </Modal>
    )}

    <ConfirmationDialog
      isOpen={isConfirmDeleteDialogOpen}
      onClose={onConfirmDeleteClose}
      onConfirm={handleConfirmDelete}
      headerText="Delete Person"
      bodyText="Are you sure you want to delete this person? This action cannot be undone."
      confirmButtonText="Delete"
      confirmButtonColorScheme="red"
    />

    {isColumnSelectorOpen && (
      <ColumnSelector<Person>
        isOpen={isColumnSelectorOpen}
        onClose={onColumnSelectorClose}
        allAvailableColumns={allAvailableColumns}
        currentVisibleColumnKeys={currentVisibleColumnKeys}
        defaultVisibleColumnKeys={defaultVisibleColumnKeys}
        onApply={(newKeys) => setVisibleColumnKeys(TABLE_KEY, newKeys)}
        onReset={() => resetTableToDefaults(TABLE_KEY, defaultVisibleColumnKeys)}
      />
    )}
    </>
  );
}

export default PeoplePage;

import React, { useEffect, useState } from 'react';
import { useAppStore } from '../stores/useAppStore';
import { usePipelinesStore, Pipeline } from '../stores/usePipelinesStore';
import { 
  Box, 
  Heading, 
  Button, 
  HStack, 
  VStack, 
  Spinner, 
  Alert, 
  AlertIcon,
  List, 
  ListItem,
  IconButton,
  useDisclosure,
  Flex,
  useToast
} from '@chakra-ui/react';
import { EditIcon, DeleteIcon, ViewIcon, CopyIcon } from '@chakra-ui/icons';
import { useNavigate } from 'react-router-dom';

// Import the modal components
import CreatePipelineModal from '../components/pipelines/CreatePipelineModal';
import EditPipelineModal from '../components/pipelines/EditPipelineModal';
import ConfirmationDialog from '../components/common/ConfirmationDialog';
import EmptyState from '../components/common/EmptyState';

const PipelinesPage: React.FC = () => {
  // Select state slices
  const { pipelines, fetchPipelines, pipelinesLoading, pipelinesError, deletePipeline: deletePipelineAction } = usePipelinesStore();

  // Fetch permissions
  const userPermissions = useAppStore((state) => state.userPermissions);

  // Disclosure hooks for the modals
  const { isOpen: isCreateOpen, onOpen: onCreateOpen, onClose: onCreateClose } = useDisclosure();
  const { isOpen: isEditOpen, onOpen: onEditOpen, onClose: onEditClose } = useDisclosure();
  const { isOpen: isDeleteOpen, onOpen: onDeleteOpen, onClose: onDeleteClose } = useDisclosure();

  // State for tracking pipeline being edited/deleted
  const [pipelineToEdit, setPipelineToEdit] = useState<Pipeline | null>(null);
  const [pipelineToDelete, setPipelineToDelete] = useState<Pipeline | null>(null);
  const [isDeleting, setIsDeleting] = useState(false);

  const navigate = useNavigate();
  const toast = useToast();

  useEffect(() => {
    // Fetch pipelines when the component mounts
    fetchPipelines();
  }, [fetchPipelines]);

  const handleViewStages = (pipelineId: string) => {
    navigate(`/pipelines/${pipelineId}/stages`);
  };

  // --- Modal Handlers ---
  const handleAddPipeline = () => {
    onCreateOpen();
  };

  const handleEditPipeline = (pipeline: Pipeline) => {
    setPipelineToEdit(pipeline);
    onEditOpen();
  };

  const handleDeletePipeline = (pipeline: Pipeline) => {
    setPipelineToDelete(pipeline);
    onDeleteOpen();
  };

  // Success callback (optional - store handles state update)
  const handleSuccess = () => {
    console.log('Pipeline action successful');
    // fetchPipelines(); // Re-fetching is usually not needed due to store updates
  };

  const handleConfirmDelete = async () => {
    if (!pipelineToDelete) return;

    setIsDeleting(true);
    const success = await deletePipelineAction(pipelineToDelete.id);
    setIsDeleting(false);
    onDeleteClose(); // Close the dialog
    setPipelineToDelete(null); // Clear selection

    if (success) {
      toast({ title: 'Pipeline deleted.', status: 'success', duration: 3000, isClosable: true });
      handleSuccess(); // Call original success handler if needed
    } else {
      // Assuming deletePipelineAction sets an error state in the store
      const errorMsg = usePipelinesStore.getState().pipelinesError || 'Failed to delete pipeline';
      toast({ title: 'Error Deleting Pipeline', description: errorMsg, status: 'error', duration: 5000, isClosable: true });
    }
  };

  const handleEditClose = () => {
      onEditClose();
      setPipelineToEdit(null); // Clear selection on close
  };

  const handleDeleteClose = () => {
      onDeleteClose();
      setPipelineToDelete(null); // Clear selection on close
  };

  return (
    <VStack spacing={4} align="stretch">
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="lg">Pipelines</Heading>
        <Button 
            onClick={handleAddPipeline} 
            colorScheme="blue"
            isDisabled={!userPermissions?.includes('pipeline:create')}
        >
          New Pipeline
        </Button>
      </Flex>

      {pipelinesLoading && (
        <Flex justify="center" align="center" minH="200px">
          <Spinner size="xl" />
        </Flex>
      )}

      {pipelinesError && (
        <Alert status="error" mb={4}>
          <AlertIcon />
          Error fetching pipelines: {pipelinesError}
        </Alert>
      )}

      {!pipelinesLoading && !pipelinesError && (
        <VStack spacing={4} align="stretch">
          {pipelines.length === 0 ? (
            <EmptyState 
              icon={CopyIcon}
              title="No Pipelines Created"
              message="Create pipelines to visualize and manage your sales processes."
              actionButtonLabel="Add Pipeline"
              onActionButtonClick={handleAddPipeline}
              isActionButtonDisabled={!userPermissions?.includes('pipeline:create')}
            />
          ) : (
            <List spacing={3}>
              {pipelines.map((pipeline) => (
                <ListItem 
                  key={pipeline.id} 
                  p={4} 
                  borderWidth="1px" 
                  borderRadius="md" 
                  bg={{ base: 'white', _dark: 'gray.700' }} 
                  borderColor={{ base: 'gray.200', _dark: 'gray.600' }}
                  shadow="sm"
                  _hover={{ shadow: 'md' }}
                >
                  <Flex justify="space-between" align="center">
                    <Box flexGrow={1} mr={4}>
                      <Heading size="md">{pipeline.name}</Heading>
                      {/* Can add more details here later, like stage count */}
                    </Box>
                    <HStack spacing={2}>
                      <Button 
                        variant="outline" 
                        size="sm" 
                        leftIcon={<ViewIcon />}
                        onClick={() => handleViewStages(pipeline.id)} 
                      >
                        View Stages
                      </Button>
                      <IconButton
                        aria-label="Edit pipeline"
                        icon={<EditIcon />}
                        size="sm"
                        variant="ghost"
                        onClick={() => handleEditPipeline(pipeline)}
                        isDisabled={!userPermissions?.includes('pipeline:update_any')}
                      />
                      <IconButton
                        aria-label="Delete pipeline"
                        icon={<DeleteIcon />}
                        size="sm"
                        variant="ghost"
                        colorScheme="red"
                        onClick={() => handleDeletePipeline(pipeline)}
                        isDisabled={!userPermissions?.includes('pipeline:delete_any')}
                      />
                    </HStack>
                  </Flex>
                </ListItem>
              ))}
            </List>
          )}
        </VStack>
      )}

      {/* Render Modals */} 
      <CreatePipelineModal 
        isOpen={isCreateOpen} 
        onClose={onCreateClose} 
        onSuccess={handleSuccess} 
      />

      <EditPipelineModal 
        isOpen={isEditOpen} 
        onClose={handleEditClose} // Use custom close handler to clear state
        pipeline={pipelineToEdit} 
        onSuccess={handleSuccess} 
      />

      <ConfirmationDialog
        isOpen={isDeleteOpen}
        onClose={handleDeleteClose} // Keep custom close to clear state
        onConfirm={handleConfirmDelete}
        headerText="Delete Pipeline"
        bodyText={`Are you sure you want to delete the pipeline "${pipelineToDelete?.name}"? All associated stages and deals within those stages will also be affected. This action cannot be undone.`}
        confirmButtonText="Delete"
        confirmButtonColorScheme="red"
        isLoading={isDeleting}
      />

    </VStack>
  );
};

export default PipelinesPage;

import { useState, useEffect } from 'react';
import { Box, Heading, Button, VStack, useToast, Spinner, Text, Flex, IconButton } from '@chakra-ui/react';
import { EditIcon, CloseIcon } from '@chakra-ui/icons';
import { gql } from 'graphql-request';
import { gqlClient } from '../lib/graphqlClient';
import { isGraphQLErrorWithMessage } from '../lib/graphqlUtils';
import type { User } from '../generated/graphql/graphql';
import ProfileView from '../components/profile/ProfileView';
import ProfileEditForm from '../components/profile/ProfileEditForm';

// Define the query string directly for graphql-request
const GET_ME_QUERY = gql`
  query GetMe {
    me {
      id
      email
      display_name
      avatar_url
    }
  }
`;

const ProfilePage: React.FC = () => {
  console.log('[ProfilePage] Component rendering/rerendering'); // LIFECYCLE LOG
  const [isEditing, setIsEditing] = useState(false);
  const toast = useToast();

  // Replace useQuery with useState and useEffect for graphql-request
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  const fetchProfile = async () => {
    console.log('[ProfilePage] fetchProfile called'); // FETCH LOG
    setLoading(true);
    setError(null);
    try {
      const response = await gqlClient.request<{ me: User | null }>(GET_ME_QUERY);
      setUser(response.me);
    } catch (err: any) {
      console.error('Error fetching profile:', err);
      let errorMessage = 'Could not load your profile data.';
      if (isGraphQLErrorWithMessage(err)) {
        errorMessage = err.response!.errors[0].message || errorMessage;
      } else if (err instanceof Error) {
        errorMessage = err.message;
      }
      setError(new Error(errorMessage)); // Store an Error object
      toast({
        title: 'Error Loading Profile',
        description: errorMessage,
        status: 'error',
        duration: 9000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  // Fetch profile data on component mount
  useEffect(() => {
    console.log('[ProfilePage] useEffect for fetchProfile triggered (dependency array: [])'); // EFFECT LOG
    fetchProfile();
  }, []); // RE-ADD dependency array: []

  const handleUpdateSuccess = (updatedUser: User) => {
    setIsEditing(false);
    setUser(updatedUser); // Update local state with the new user data
    // If ProfileEditForm also uses graphql-request, this is fine.
    // If it were using Apollo, it might update cache, but here we manually set user.
    console.log('Profile updated successfully in page:', updatedUser);
    toast({
      title: 'Profile Updated',
      description: 'Your profile has been successfully updated.',
      status: 'success',
      duration: 5000,
      isClosable: true,
    });
  };

  if (error && !user) { // Show error only if there's no data to potentially display
    return (
      <Box textAlign="center" py={10} px={6}>
        <Heading as="h2" size="xl" mt={6} mb={2} color="red.500">
          Error Loading Profile
        </Heading>
        <Text color={'gray.500'}>
          {error.message || 'We encountered an issue loading your profile. Please try again later.'}
        </Text>
      </Box>
    );
  }

  // If loading and no data yet, show global spinner. If loading but data exists (e.g. refetching), ProfileView can show its own spinner.
  if (loading && !user) {
    return (
      <Flex justify="center" align="center" minH="200px">
        <Spinner size="xl" />
      </Flex>
    );
  }

  return (
    <Box p={6} maxWidth="container.md" mx="auto">
      <VStack spacing={6} align="stretch">
        <Flex justifyContent="space-between" alignItems="center">
          <Heading as="h1" size="xl">
            My Profile
          </Heading>
          {user && !isEditing && (
            <IconButton
              aria-label="Edit Profile"
              icon={<EditIcon />}
              onClick={() => setIsEditing(true)}
              variant="ghost"
            />
          )}
          {isEditing && (
             <IconButton
              aria-label="Cancel Edit"
              icon={<CloseIcon />}
              onClick={() => setIsEditing(false)}
              variant="ghost"
            />
          )}
        </Flex>

        {isEditing && user ? (
          <ProfileEditForm
            user={user}
            onUpdateSuccess={handleUpdateSuccess}
            onCancel={() => setIsEditing(false)}
          />
        ) : (
          <ProfileView user={user} isLoading={loading} />
        )}
      </VStack>
    </Box>
  );
};

export default ProfilePage;

import React, { useEffect, useState } from 'react';
import { useParams, Link as RouterLink } from 'react-router-dom';
import { useAppStore } from '../stores/useAppStore';
import { usePipelinesStore, Pipeline } from '../stores/usePipelinesStore';
import { useStagesStore, Stage } from '../stores/useStagesStore';
import { 
  Box, 
  Heading, 
  Text, 
  Button, 
  HStack, 
  VStack, 
  Spinner, 
  Alert, 
  AlertIcon,
  List, 
  ListItem,
  IconButton,
  Link,
  Flex,
  useDisclosure,
  useToast
} from '@chakra-ui/react';
import { EditIcon, DeleteIcon, ArrowBackIcon, DragHandleIcon } from '@chakra-ui/icons';

// Import the stage modal components
import CreateStageModal from '../components/stages/CreateStageModal';
import EditStageModal from '../components/stages/EditStageModal';
import ConfirmationDialog from '../components/common/ConfirmationDialog';
import EmptyState from '../components/common/EmptyState';

// TODO: Import/create Modals for Create/Edit/Delete Stages later

const StagesPage: React.FC = () => {
  const { pipelineId } = useParams<{ pipelineId: string }>();

  // Add this state to track fetch attempts per pipeline
  const [initialFetchAttemptedForPipeline, setInitialFetchAttemptedForPipeline] = useState<Record<string, boolean>>({});

  // Select state slices from Zustand
  const { 
    stages,
    fetchStages,
    stagesLoading,
    stagesError,
    deleteStage: deleteStageAction
  } = useStagesStore();

  const userPermissions = useAppStore((state) => state.userPermissions);

  // Get pipelines state from usePipelinesStore
  const { 
    pipelines, 
    fetchPipelines: fetchPipelinesFromStore,
    pipelinesLoading: pipelinesLoadingFromStore,
    selectedPipelineId: selectedPipelineIdFromStore
  } = usePipelinesStore();

  const currentPipeline = pipelines.find(p => p.id === pipelineId);

  // State for modals
  const { isOpen: isCreateOpen, onOpen: onCreateOpen, onClose: onCreateClose } = useDisclosure();
  const { isOpen: isEditOpen, onOpen: onEditOpen, onClose: onEditClose } = useDisclosure();
  const { isOpen: isDeleteOpen, onOpen: onDeleteOpen, onClose: onDeleteClose } = useDisclosure();
  const [stageToEdit, setStageToEdit] = useState<Stage | null>(null);
  const [stageToDelete, setStageToDelete] = useState<Stage | null>(null);
  const [isDeleting, setIsDeleting] = useState(false);
  const toast = useToast();

  useEffect(() => {
    if (pipelines.length === 0 && !pipelinesLoadingFromStore && pipelineId) {
        fetchPipelinesFromStore(); 
    }

    if (pipelineId && !stagesLoading) {
      // Check if fetch has been attempted for the current pipelineId
      // Simplified: always attempt fetch if pipelineId is present and not currently loading stages
      // More sophisticated logic might be needed if fetchStages itself sets stagesError and we need to distinguish
      // fetch errors from action errors more granularly for display.
      // For now, the store's stagesError will reflect the latest error (fetch or action).
      fetchStages(pipelineId);
    }
    // Intentionally simplified dependencies for refetch on pipelineId change.
    // Consider adding initialFetchAttemptedForPipeline if you want to prevent refetching on every render.
  }, [pipelineId, fetchPipelinesFromStore]); // Removed some dependencies to make it simpler for now

  // --- Modal Handlers ---
  const handleAddStage = () => {
    onCreateOpen();
  };

  const handleEditStage = (stage: Stage) => {
    setStageToEdit(stage);
    onEditOpen();
  };

  const handleDeleteStage = (stage: Stage) => {
    setStageToDelete(stage);
    onDeleteOpen();
  };

  const handleSuccess = () => {
    console.log('Stage action successful');
    // Store handles state updates, no refetch needed here typically
  };

  const handleEditClose = () => {
      onEditClose();
      setStageToEdit(null);
  };

  const handleDeleteClose = () => {
      onDeleteClose();
      setStageToDelete(null);
  };

  const handleConfirmDelete = async () => {
    if (!stageToDelete) return;

    setIsDeleting(true);
    // We use a local variable for the error from this specific delete action
    let deleteError: string | null = null; 
    try {
    const success = await deleteStageAction(stageToDelete.id);
      if (success) {
        toast({ title: 'Stage deleted.', status: 'success', duration: 3000, isClosable: true });
      } else {
        // If deleteStageAction returns false, it implies it caught an error and set stagesError in the store
        deleteError = useStagesStore.getState().stagesError || 'Failed to delete stage';
      }
    } catch (e) {
      // If deleteStageAction itself throws (less likely if it catches internally)
      deleteError = (e instanceof Error) ? e.message : String(e);
    } finally {
    setIsDeleting(false);
    onDeleteClose();
    setStageToDelete(null);
      if (deleteError) {
        toast({ title: 'Error Deleting Stage', description: deleteError, status: 'error', duration: 5000, isClosable: true });
      }
    }
  };

  return (
    <VStack spacing={4} align="stretch">
       <Link as={RouterLink} to="/pipelines" mb={4} display="inline-block">
         <ArrowBackIcon mr={1} /> Back to Pipelines
       </Link>
      <Heading size="lg" mb={2}>
        Stages for Pipeline: {currentPipeline ? currentPipeline.name : (pipelineId ? `(${pipelineId.substring(0, 8)}...)` : 'Loading...')}
      </Heading>

      {/* Display global stagesError if present (could be from fetch or last action) */}
      {/* This provides general feedback. Specific action errors are also handled by toasts in modals/handlers. */}
      {stagesError && (
        <Alert status="error" mb={4}>
          <AlertIcon />
          {stagesError} {/* Display the error from the store */}
        </Alert>
      )}

      {pipelineId && (
        <Button 
          onClick={handleAddStage} 
          colorScheme="blue"
          alignSelf="flex-start"
          mb={4}
          isDisabled={!userPermissions?.includes('stage:create')}
        >
          New Stage
        </Button>
      )}

      {/* Show spinner only if loading and no stages are displayed yet */}
      {stagesLoading && stages.filter((s: Stage) => s.pipeline_id === pipelineId).length === 0 && (
        <Flex justify="center" align="center" minH="200px">
          <Spinner size="xl" />
        </Flex>
      )}

      {!pipelineId && !stagesLoading && ( // If no pipelineId is in the URL
          <Alert status="warning" mb={4}>
            <AlertIcon />
            No Pipeline ID found in URL. Please select a pipeline.
          </Alert>
      )}

      {/* Render stages list if pipelineId exists. 
          The list visibility is no longer strictly tied to !stagesError.
          If stagesError is from a fetch, stages array would likely be empty.
          If stagesError is from an action (like create), stages array still holds existing data.
      */}
      {pipelineId && (
        // Filter stages for the current pipeline from the store's potentially mixed list
        stages.filter((s: Stage) => s.pipeline_id === pipelineId).length === 0 && !stagesLoading ? (
          // Show EmptyState only if, after filtering, there are no stages for THIS pipeline, AND not currently loading.
          // And no critical fetch error prevented any stage loading (the Alert above would show stagesError).
            <EmptyState 
              icon={DragHandleIcon}
              title="No Stages in this Pipeline"
              message="Add stages to define the steps in your sales process for this pipeline."
              actionButtonLabel="Add Stage"
              onActionButtonClick={handleAddStage}
              isActionButtonDisabled={!userPermissions?.includes('stage:create')}
            />
          ) : (
            <List spacing={3}>
              {stages.filter((s: Stage) => s.pipeline_id === pipelineId).map((stage: Stage) => (
                <ListItem 
                  key={stage.id} 
                  p={4} 
                  borderWidth="1px" 
                  borderRadius="md" 
                  bg={{ base: 'white', _dark: 'gray.700' }} 
                  borderColor={{ base: 'gray.200', _dark: 'gray.600' }}
                  shadow="sm"
                  _hover={{ shadow: 'md' }}
                >
                  <Flex justify="space-between" align="center">
                    <Box flexGrow={1} mr={4}>
                      <Heading size="sm">{stage.name}</Heading>
                      <Text fontSize="sm" color={{ base: 'gray.500', _dark: 'gray.400' }}>
                        Order: {stage.order}
                        {stage.deal_probability !== null && stage.deal_probability !== undefined && 
                          // Multiply by 100 and format as percentage
                          ` | Probability: ${Math.round(stage.deal_probability * 100)}%` 
                        }
                      </Text>
                    </Box>
                    <HStack spacing={2}>
                      <IconButton
                        aria-label="Edit stage"
                        icon={<EditIcon />}
                        size="sm"
                        variant="ghost"
                        onClick={() => handleEditStage(stage)}
                        isDisabled={!userPermissions?.includes('stage:update_any')}
                      />
                      <IconButton
                        aria-label="Delete stage"
                        icon={<DeleteIcon />}
                        size="sm"
                        variant="ghost"
                        colorScheme="red"
                        onClick={() => handleDeleteStage(stage)}
                        isDisabled={!userPermissions?.includes('stage:delete_any')}
                      />
                    </HStack>
                  </Flex>
                </ListItem>
              ))}
            </List>
        )
      )}

      {pipelineId && (
        <CreateStageModal 
          isOpen={isCreateOpen} 
          onClose={onCreateClose} 
          pipelineId={pipelineId} 
          onSuccess={handleSuccess} // onSuccess might trigger a toast or a silent success log
        />
      )}

      <EditStageModal 
        isOpen={isEditOpen} 
        onClose={handleEditClose} 
        stage={stageToEdit} 
        onSuccess={handleSuccess} 
      />

      <ConfirmationDialog
        isOpen={isDeleteOpen}
        onClose={handleDeleteClose}
        onConfirm={handleConfirmDelete}
        headerText="Delete Stage"
        bodyText={`Are you sure you want to delete the stage "${stageToDelete?.name}"? All deals in this stage will be affected. This action cannot be undone.`}
        confirmButtonText="Delete"
        confirmButtonColorScheme="red"
        isLoading={isDeleting}
      />
    </VStack>
  );
};

