const GET_MY_PERMISSIONS_QUERY = gql`query GetMyPermissions { myPermissions }`;

const GET_DEAL_WITH_HISTORY_QUERY = gql`
  query GetDealWithHistory($dealId: ID!) {
    deal(id: $dealId) {
      id
      name
      amount
      expected_close_date
      created_at
