  user_id?: string; // user_id is added by create, but not typically in DealInput for updates from client
  weighted_amount?: number | null;
  custom_field_values?: Record<string, any> | null; // Added for direct DB update
}

// --- Deal Service ---
export const dealService = {
  getDeals: (userId: string, accessToken: string): Promise<Deal[]> => 
    getDealsInternal(userId, accessToken),

  getDealById: (userId: string, id: string, accessToken: string): Promise<Deal | null> => 
    getDealByIdInternal(userId, id, accessToken),

  getDealsByPersonId: (personId: string, accessToken: string): Promise<Deal[]> =>
    getDealsByPersonIdInternal(personId, accessToken),

  createDeal: (userId: string, input: DealInput, accessToken: string): Promise<Deal> => 
    createDealInternal(userId, input, accessToken),

  updateDeal: (userId: string, id: string, input: Partial<DealInput>, accessToken: string): Promise<Deal> => 
    updateDealInternal(userId, id, input, accessToken),

  deleteDeal: (userId: string, id: string, accessToken: string): Promise<boolean> => 
    deleteDealInternal(userId, id, accessToken),
};

import { GraphQLError } from 'graphql';
import { getAuthenticatedClient, handleSupabaseError, recordEntityHistory } from '../serviceUtils.js';
import type { Deal, DealInput, CustomFieldValueInput } from '../generated/graphql.js';
import { inngest } from '../inngestClient.js';

import { processCustomFieldsForCreate, processCustomFieldsForUpdate } from './dealCustomFields.js';
import { calculateDealProbabilityFields } from './dealProbability.js';
import { generateDealChanges } from './dealHistory.js';

// Define a more specific type for the update payload, similar to original dealService.ts
interface DealUpdatePayload extends Partial<DealInput> {
  user_id?: string; 
  weighted_amount?: number | null;
  custom_field_values?: Record<string, any> | null;
  deal_specific_probability?: number | null; // Explicitly added
}

// --- Deal CRUD Operations ---

export async function getDeals(userId: string, accessToken: string): Promise<Deal[]> {
  console.log('[dealCrud.getDeals] called for user:', userId);
  const supabase = getAuthenticatedClient(accessToken);
  const { data, error } = await supabase
    .from('deals')
    .select('*')
    .order('created_at', { ascending: false });

  handleSupabaseError(error, 'fetching deals');
  return (data || []) as Deal[];
}

export async function getDealsByPersonId(personId: string, accessToken: string): Promise<Deal[]> {
  console.log('[dealCrud.getDealsByPersonId] called for personId:', personId);
  const supabase = getAuthenticatedClient(accessToken);
  const { data, error } = await supabase
    .from('deals')
    .select('*')
    .eq('person_id', personId)
    .order('created_at', { ascending: false });

  handleSupabaseError(error, 'fetching deals by person_id');
  return (data || []) as Deal[];
}

export async function getDealById(userId: string, id: string, accessToken:string): Promise<Deal | null> {
  console.log('[dealCrud.getDealById] called for user:', userId, 'id:', id);
  const supabase = getAuthenticatedClient(accessToken);
  const { data, error } = await supabase
    .from('deals')
    .select('*')
    .eq('id', id)
    .single();

  if (error && error.code !== 'PGRST116') { 
     handleSupabaseError(error, 'fetching deal by ID');
  }
  return data;
}

export async function createDeal(userId: string, input: DealInput, accessToken: string): Promise<Deal> {
  console.log('[dealCrud.createDeal] called for user:', userId, 'input:', JSON.stringify(input, null, 2));
  const supabase = getAuthenticatedClient(accessToken);

  const { pipeline_id, customFields, ...dealCoreData } = input; // pipeline_id is part of DealInput, used by schema/Zod, but not directly on 'deals' table

  const processedCustomFields = await processCustomFieldsForCreate(customFields, supabase);

  const insertPayload: any = { 
    ...dealCoreData, 
    user_id: userId,
    // Ensure pipeline_id is not in insertPayload if it's not a direct column on 'deals' table
    // It's used for validation/association elsewhere, not part of the direct deals table schema usually.
    // If pipeline_id IS a direct column, this can be adjusted.
    // Based on current DealInput, pipeline_id and stage_id are required.
    // stage_id is on deals table, pipeline_id might be for stage validation.
    // Assuming pipeline_id is not directly inserted into deals table from here.
  };
  // Remove pipeline_id if it was destructured into dealCoreData and is not a direct column.
  // It's safer to explicitly build the payload if there's ambiguity.
  // Let's rebuild dealCoreData for insert to be explicit:
  const explicitDealCoreData: Partial<DealInput> = {
    name: input.name,
    stage_id: input.stage_id, // This is on deals table
    amount: input.amount,
    expected_close_date: input.expected_close_date,
    person_id: input.person_id,
    organization_id: input.organization_id,
    deal_specific_probability: input.deal_specific_probability,
    // Do NOT include pipeline_id here if it's not a direct column of 'deals' table
  };

  const finalInsertPayload: any = {
    ...explicitDealCoreData,
    user_id: userId,
    custom_field_values: processedCustomFields, // This can be null
  };

  console.log('[dealCrud.createDeal] Final insert payload for Supabase:', JSON.stringify(finalInsertPayload, null, 2));

  const { data: newDealRecord, error } = await supabase
    .from('deals')
    .insert(finalInsertPayload)
    .select()
    .single();

  handleSupabaseError(error, 'creating deal');
  if (!newDealRecord) {
      throw new GraphQLError('Failed to create deal, no data returned', { extensions: { code: 'INTERNAL_SERVER_ERROR' } });
  }

  const initialChanges: Record<string, any> = {};
  const relevantFieldsForCreate: (keyof Deal)[] = ['name', 'stage_id', 'amount', 'expected_close_date', 'person_id', 'organization_id', 'deal_specific_probability'];
  relevantFieldsForCreate.forEach(key => {
    const stringKey = key as string; // Ensure key is treated as string for indexing
    if (newDealRecord[stringKey] !== undefined && newDealRecord[stringKey] !== null) {
      initialChanges[stringKey] = newDealRecord[stringKey];
    }
  });
  // Also consider adding created custom fields to initialChanges if needed.
  if (newDealRecord.custom_field_values) {
    initialChanges.custom_field_values = newDealRecord.custom_field_values;
  }

  await recordEntityHistory(
    supabase,
    'deal_history',
    'deal_id',
    newDealRecord.id,
    userId,
    'DEAL_CREATED',
    initialChanges
  );
  return newDealRecord as Deal;
}

export async function updateDeal(userId: string, id: string, input: Partial<DealInput>, accessToken: string): Promise<Deal> {
  console.log('[dealCrud.updateDeal] called for user:', userId, 'id:', id, 'input:', JSON.stringify(input, null, 2));
  const supabase = getAuthenticatedClient(accessToken); 

  const { customFields: inputCustomFields, ...coreInput } = input;

  // Initial check: if coreInput is empty AND there are no customFields to process, then it's an empty update.
  if (Object.keys(coreInput).length === 0 && (inputCustomFields === undefined || (Array.isArray(inputCustomFields) && inputCustomFields.length === 0))) {
      throw new GraphQLError('No fields provided for deal update.', { extensions: { code: 'BAD_USER_INPUT' } });
  }

  const oldDealData = await getDealById(userId, id, accessToken);
  if (!oldDealData) {
      throw new GraphQLError('Deal not found for update', { extensions: { code: 'NOT_FOUND' } });
  }

  // Start with coreInput. Supabase client will ignore undefined fields.
  const dbUpdatePayload: DealUpdatePayload = { ...coreInput }; 

  // Process custom fields if the key 'customFields' was provided in the input
  if (inputCustomFields !== undefined) { 
      const { finalCustomFieldValues } = await processCustomFieldsForUpdate(
          (oldDealData as any).custom_field_values || null,
          inputCustomFields, // can be null or empty array if key was present
          supabase
      );
      // Assign regardless of whether finalCustomFieldValues is null, to allow clearing custom fields.
      dbUpdatePayload.custom_field_values = finalCustomFieldValues;
  }

  // Calculate and add probability fields
  const probabilityUpdates = await calculateDealProbabilityFields(
      coreInput, // Contains potential stage_id or amount changes
      oldDealData,
      supabase
  );
  if (probabilityUpdates.deal_specific_probability_to_set !== undefined) {
      dbUpdatePayload.deal_specific_probability = probabilityUpdates.deal_specific_probability_to_set;
  }
  if (probabilityUpdates.weighted_amount_to_set !== undefined) {
      dbUpdatePayload.weighted_amount = probabilityUpdates.weighted_amount_to_set;
  }

  // Ensure pipeline_id is not part of the direct update payload to 'deals' table
  if ('pipeline_id' in dbUpdatePayload) {
      delete (dbUpdatePayload as any).pipeline_id;
  }

  // Check if there's anything to update after all processing.
  // We need to be careful: dbUpdatePayload might contain only { custom_field_values: null }
  // which IS an update. Or { deal_specific_probability: null } which is also an update.
  // An empty object {} means no fields are being explicitly changed.
  let hasActualUpdate = false;
  for (const _key in dbUpdatePayload) {
      hasActualUpdate = true;
      break;
  }

  if (!hasActualUpdate) {
    console.log('[dealCrud.updateDeal] No actual fields to update in DB payload after processing. Returning old deal data.');
    return oldDealData; 
  }

  console.log('[dealCrud.updateDeal] Final DB update payload:', JSON.stringify(dbUpdatePayload, null, 2));
  const { data: updatedDeal, error } = await supabase
    .from('deals')
    .update(dbUpdatePayload) // Pass dbUpdatePayload directly
    .eq('id', id)
    .select()
    .single();

  handleSupabaseError(error, 'updating deal');
   if (!updatedDeal) { 
      throw new GraphQLError('Deal update failed, no data returned', { extensions: { code: 'INTERNAL_SERVER_ERROR' } });
  }

  const actualChanges = generateDealChanges(oldDealData, updatedDeal as Deal);

  if (Object.keys(actualChanges).length > 0) {
    console.log('[dealCrud.updateDeal] Recording history with changes:', JSON.stringify(actualChanges, null, 2));
    await recordEntityHistory(
      supabase,
      'deal_history',
      'deal_id',
      id,
      userId,
      'DEAL_UPDATED',
      actualChanges
    );

    try {
      await inngest.send({
        name: 'crm/deal.updated',
        user: { id: userId }, 
        data: {
          dealId: id,
          updatedFields: Object.keys(actualChanges),
          changes: actualChanges, 
        },
      });
      console.log(`[dealCrud.updateDeal] Sent 'crm/deal.updated' event for deal ID: ${id}`);
    } catch (eventError: any) {
      console.error(`[dealCrud.updateDeal] Failed to send 'crm/deal.updated' event for deal ID: ${id}:`, eventError.message);
    }
  } else {
      console.log('[dealCrud.updateDeal] No actual changes detected to record in history.');
  }

  return updatedDeal as Deal;
}

export async function deleteDeal(userId: string, id: string, accessToken: string): Promise<boolean> {
  console.log('[dealCrud.deleteDeal] called for user:', userId, 'id:', id);
  const supabase = getAuthenticatedClient(accessToken);

  const { error, count } = await supabase
    .from('deals')
    .delete()
    .eq('id', id);

  handleSupabaseError(error, 'deleting deal');

  if (!error) {
    await recordEntityHistory(
      supabase,
      'deal_history',
      'deal_id',
      id,
      userId,
      'DEAL_DELETED',
      { deleted_deal_id: id }
    );
  }

  console.log('[dealCrud.deleteDeal] Deleted count (informational):', count);
  return !error;
}

import type { CustomFieldValueInput } from '../generated/graphql';
import type { SupabaseClient } from '@supabase/supabase-js';
import { getCustomFieldDefinitionById } from '../customFieldDefinitionService';

/**
 * Processes custom field inputs for deal creation.
 * @param customFieldsInput Array of custom field value inputs.
 * @param supabaseClient Authenticated Supabase client instance.
 * @returns A record of custom field names to their values, or null if no valid custom fields processed.
 */
export const processCustomFieldsForCreate = async (
  customFieldsInput: CustomFieldValueInput[] | undefined | null,
  supabaseClient: SupabaseClient
): Promise<Record<string, any> | null> => {
  if (!customFieldsInput || customFieldsInput.length === 0) {
    return null;
  }

  const dbCustomFieldValues: Record<string, any> = {};
  console.log('[dealCustomFields.processCustomFieldsForCreate] Processing customFields input count:', customFieldsInput.length);

  for (const cfInput of customFieldsInput) {
    try {
      const definition = await getCustomFieldDefinitionById(supabaseClient, cfInput.definitionId);
      if (definition) {
        const fieldName = definition.fieldName;
        let valueToStore: any = undefined; 

        if (cfInput.stringValue !== undefined && cfInput.stringValue !== null) valueToStore = cfInput.stringValue;
        else if (cfInput.numberValue !== undefined && cfInput.numberValue !== null) valueToStore = cfInput.numberValue;
        else if (cfInput.booleanValue !== undefined && cfInput.booleanValue !== null) valueToStore = cfInput.booleanValue;
        else if (cfInput.dateValue !== undefined && cfInput.dateValue !== null) valueToStore = cfInput.dateValue; 
        else if (cfInput.selectedOptionValues !== undefined && cfInput.selectedOptionValues !== null) valueToStore = cfInput.selectedOptionValues;

        if (valueToStore !== undefined) {
             dbCustomFieldValues[fieldName] = valueToStore;
             console.log(`[dealCustomFields.processCustomFieldsForCreate] Storing for ${fieldName}:`, valueToStore);
        } else {
             console.log(`[dealCustomFields.processCustomFieldsForCreate] No value provided for custom field based on definition ${definition.id} (${fieldName}), skipping.`);
        }
      } else {
        console.warn(`[dealCustomFields.processCustomFieldsForCreate] Custom field definition ${cfInput.definitionId} not found. Skipping.`);
      }
    } catch (defError: any) {
        console.error(`[dealCustomFields.processCustomFieldsForCreate] Error fetching/processing definition ${cfInput.definitionId}:`, defError.message);
    }
  }

  console.log('[dealCustomFields.processCustomFieldsForCreate] Constructed dbCustomFieldValues:', JSON.stringify(dbCustomFieldValues, null, 2));
  return Object.keys(dbCustomFieldValues).length > 0 ? dbCustomFieldValues : null;
};

/**
 * Processes custom field inputs for deal updates.
 * @param currentDbCustomFieldValues The existing custom field values from the database for the deal.
 * @param customFieldsInput Array of custom field value inputs for update.
 * @param supabaseClient Authenticated Supabase client instance.
 * @returns An object containing the final custom field values to be stored in the database.
 */
export const processCustomFieldsForUpdate = async (
  currentDbCustomFieldValues: Record<string, any> | null,
  customFieldsInput: CustomFieldValueInput[] | undefined | null,
  supabaseClient: SupabaseClient
): Promise<{ finalCustomFieldValues: Record<string, any> | null }> => {
  let finalCustomFieldValues: Record<string, any> | null = currentDbCustomFieldValues || {};

  if (!customFieldsInput || customFieldsInput.length === 0) {
    // If no new input, return current values (could be null if nothing existed)
    return { finalCustomFieldValues: finalCustomFieldValues }; 
  }

  console.log('[dealCustomFields.processCustomFieldsForUpdate] Processing customFields input count:', customFieldsInput.length);
  const customFieldsToUpdate: Record<string, any> = {};

  for (const cfInput of customFieldsInput) {
    try {
      const definition = await getCustomFieldDefinitionById(supabaseClient, cfInput.definitionId);
      if (definition) {
        const fieldName = definition.fieldName;
        let valueToStore: any = null; // Default to null for updates, to allow clearing fields

        if ('stringValue' in cfInput) valueToStore = cfInput.stringValue;
        else if ('numberValue' in cfInput) valueToStore = cfInput.numberValue;
        else if ('booleanValue' in cfInput) valueToStore = cfInput.booleanValue;
        else if ('dateValue' in cfInput) valueToStore = cfInput.dateValue;
        else if ('selectedOptionValues' in cfInput) valueToStore = cfInput.selectedOptionValues;

        customFieldsToUpdate[fieldName] = valueToStore;
        console.log(`[dealCustomFields.processCustomFieldsForUpdate] Queuing update for custom field ${fieldName}:`, valueToStore);
      } else {
        console.warn(`[dealCustomFields.processCustomFieldsForUpdate] Custom field definition ${cfInput.definitionId} not found. Skipping.`);
      }
    } catch (defError: any) {
        console.error(`[dealCustomFields.processCustomFieldsForUpdate] Error fetching/processing definition ${cfInput.definitionId}:`, defError.message);
    }
  }

  finalCustomFieldValues = { ...(currentDbCustomFieldValues || {}), ...customFieldsToUpdate };
  console.log('[dealCustomFields.processCustomFieldsForUpdate] Merged custom_field_values:', JSON.stringify(finalCustomFieldValues, null, 2));

  return { finalCustomFieldValues };
};

import type { Deal } from '../generated/graphql';
import { diff, Diff } from 'deep-diff';

// Define which fields are tracked for history purposes.
// Add 'custom_field_values' as it's a special case handled in diffing.
export const TRACKED_DEAL_FIELDS: (keyof Deal | 'custom_field_values')[] = [
  'name', 
  'stage_id', 
  'amount', 
  'expected_close_date', 
  'person_id', 
  'organization_id', 
  'deal_specific_probability',
  'custom_field_values' 
];

/**
 * Generates a structured diff of changes between old and new deal data.
 * @param oldDealData The original deal data.
 * @param updatedDealData The new deal data after updates.
 * @returns A record of actual changes, or an empty object if no tracked changes are found.
 */
export const generateDealChanges = (
  oldDealData: Deal, 
  updatedDealData: Deal
): Record<string, { oldValue: any; newValue: any }> => {
  const oldDealForDiff = oldDealData as any; // Cast to handle custom_field_values
  const updatedDealForDiff = updatedDealData as any; // Cast to handle custom_field_values

  const differences: Diff<any, any>[] | undefined = diff(oldDealForDiff, updatedDealForDiff);
  const actualChanges: Record<string, { oldValue: any; newValue: any }> = {};

  if (differences) {
    differences.forEach(d => {
      if (d.path && d.path.length > 0) {
        const key = d.path[0] as keyof Deal | 'custom_field_values';

        if (TRACKED_DEAL_FIELDS.includes(key)) {
          if (key === 'custom_field_values') {
            // Handle changes within the custom_field_values JSON object
            if (d.path.length === 2) { // e.g., ['custom_field_values', 'fieldName']
              const customFieldName = d.path[1] as string;
              const oldValue = oldDealForDiff.custom_field_values ? oldDealForDiff.custom_field_values[customFieldName] : undefined;
              const newValue = updatedDealForDiff.custom_field_values ? updatedDealForDiff.custom_field_values[customFieldName] : undefined;

              if (JSON.stringify(oldValue) !== JSON.stringify(newValue)) {
                actualChanges[`custom_field_values.${customFieldName}`] = { oldValue, newValue };
              }
            } else if (d.kind === 'A' && d.item && d.item.kind) { 
              // This handles changes to array elements within custom_field_values (e.g. for MULTI_SELECT)
              // For simplicity, we'll record the whole custom_field_values object if an array within it changes.
              const fullOldCFValues = oldDealForDiff.custom_field_values || {};
              const fullNewCFValues = updatedDealForDiff.custom_field_values || {};
              if (JSON.stringify(fullOldCFValues) !== JSON.stringify(fullNewCFValues) && !actualChanges['custom_field_values']) { // Avoid overwriting granular changes if already recorded
                  actualChanges['custom_field_values'] = { oldValue: fullOldCFValues, newValue: fullNewCFValues };
              }
            } else if (d.kind === 'N' || d.kind === 'D' || d.kind === 'E') {
              // This handles cases where the entire custom_field_values object is new, deleted, or edited 
              // Or a top-level key within custom_field_values is added/deleted/edited.
               const fullOldCFValues = oldDealForDiff.custom_field_values || {};
               const fullNewCFValues = updatedDealForDiff.custom_field_values || {};
               if (JSON.stringify(fullOldCFValues) !== JSON.stringify(fullNewCFValues)) {
                  actualChanges['custom_field_values'] = { oldValue: fullOldCFValues, newValue: fullNewCFValues };
               }
            }
          } else {
            // Standard flat field change
            const oldValue = oldDealForDiff[key];
            const newValue = updatedDealForDiff[key];
            if (JSON.stringify(oldValue) !== JSON.stringify(newValue)) {
              actualChanges[key as string] = { oldValue, newValue };
            }
          }
        }
      }
    });
  }
  return actualChanges;
};

import type { Deal, DealInput } from '../generated/graphql';
import type { SupabaseClient } from '@supabase/supabase-js';
import { handleSupabaseError } from '../serviceUtils'; // For consistent error handling
import { GraphQLError } from 'graphql';

interface DealProbabilityCalculationResult {
  deal_specific_probability_to_set?: number | null;
  weighted_amount_to_set?: number | null;
}

/**
 * Calculates deal probability and weighted amount based on stage or amount changes.
 * @param dealUpdateInput The partial input for the deal update.
 * @param oldDealData The current state of the deal from the database.
 * @param supabaseClient Authenticated Supabase client instance.
 * @returns An object with `deal_specific_probability_to_set` and `weighted_amount_to_set`.
 */
export const calculateDealProbabilityFields = async (
  dealUpdateInput: Partial<DealInput>,
  oldDealData: Deal, 
  supabaseClient: SupabaseClient
): Promise<DealProbabilityCalculationResult> => {
  const result: DealProbabilityCalculationResult = {};

  // 1. Handle stage change and associated probability adjustments
  if (dealUpdateInput.stage_id && dealUpdateInput.stage_id !== oldDealData.stage_id) {
    console.log(`[dealProbability.calculate] Stage changed from ${oldDealData.stage_id} to ${dealUpdateInput.stage_id}`);
    const { data: newStage, error: stageError } = await supabaseClient
        .from('stages')
        .select('stage_type, deal_probability') 
        .eq('id', dealUpdateInput.stage_id)
        .single();

    if (stageError) {
        handleSupabaseError(stageError, `fetching new stage ${dealUpdateInput.stage_id} for deal update`);
        // Throwing here ensures the operation stops if the target stage is invalid.
        throw new GraphQLError(`Target stage ${dealUpdateInput.stage_id} not found.`, { extensions: { code: 'BAD_USER_INPUT' } });
    }

    if (newStage) {
        console.log('[dealProbability.calculate] New stage properties:', newStage);
        let effectiveProbability: number | null = null;

        if (newStage.stage_type === 'WON') {
            result.deal_specific_probability_to_set = 1.0;
            effectiveProbability = 1.0;
            console.log('[dealProbability.calculate] Setting deal_specific_probability to 1.0 for WON stage');
        } else if (newStage.stage_type === 'LOST') {
            result.deal_specific_probability_to_set = 0.0;
            effectiveProbability = 0.0;
            console.log('[dealProbability.calculate] Setting deal_specific_probability to 0.0 for LOST stage');
        } else { // OPEN stage
            result.deal_specific_probability_to_set = null; 
            effectiveProbability = newStage.deal_probability; 
            console.log('[dealProbability.calculate] Clearing deal_specific_probability for OPEN stage, effective probability from stage:', effectiveProbability);
        }

        const currentAmount = typeof dealUpdateInput.amount === 'number' ? dealUpdateInput.amount : oldDealData.amount;
        if (typeof currentAmount === 'number' && effectiveProbability !== null) {
            result.weighted_amount_to_set = currentAmount * effectiveProbability;
            console.log('[dealProbability.calculate] Calculated weighted_amount:', result.weighted_amount_to_set);
        } else {
            result.weighted_amount_to_set = null; 
            console.log('[dealProbability.calculate] Setting weighted_amount to null due to missing amount or probability for new stage.');
        }
    } else {
         console.warn(`[dealProbability.calculate] New stage ${dealUpdateInput.stage_id} not found, probability not adjusted. Weighted amount not calculated.`);
    }
  // 2. Handle amount change if stage did not change (or if stage change logic didn't set weighted_amount)
  } else if (typeof dealUpdateInput.amount === 'number' && dealUpdateInput.amount !== oldDealData.amount) {
    console.log('[dealProbability.calculate] Amount changed. Recalculating weighted_amount.');
    let existingEffectiveProbability: number | null = null;

    // Use the probability that would result from the current update input if stage also changed,
    // otherwise use old deal's probability logic.
    if (result.deal_specific_probability_to_set !== undefined) { // Stage change has determined new specific probability
        if (result.deal_specific_probability_to_set === 1.0 || result.deal_specific_probability_to_set === 0.0) {
            existingEffectiveProbability = result.deal_specific_probability_to_set;
        } else { // deal_specific_probability is null (OPEN stage), need stage's default probability
            // This requires fetching the stage if stage_id isn't changing or already fetched. This is complex if stage is not changing.
            // For simplicity, if stage is not changing, use existing oldDealData probability logic.
            // This case is primarily when ONLY amount changes.
            if (oldDealData.deal_specific_probability !== null && oldDealData.deal_specific_probability !== undefined) {
                existingEffectiveProbability = oldDealData.deal_specific_probability;
            } else if (oldDealData.stage_id) {
                const { data: currentStage } = await supabaseClient
                    .from('stages')
                    .select('deal_probability, stage_type') 
                    .eq('id', oldDealData.stage_id)
                    .single(); // Error handling might be needed here too
                if (currentStage) {
                    if (currentStage.stage_type === 'WON') existingEffectiveProbability = 1.0;
                    else if (currentStage.stage_type === 'LOST') existingEffectiveProbability = 0.0;
                    else existingEffectiveProbability = currentStage.deal_probability;
                }
            }
        }
    } else { // Stage is not changing, use old deal's probability context
        if (oldDealData.deal_specific_probability !== null && oldDealData.deal_specific_probability !== undefined) {
            existingEffectiveProbability = oldDealData.deal_specific_probability;
        } else if (oldDealData.stage_id) {
            const { data: currentStage, error: currentStageError } = await supabaseClient
                .from('stages')
                .select('deal_probability, stage_type') 
                .eq('id', oldDealData.stage_id)
                .single();

            if (currentStage && !currentStageError) {
                if (currentStage.stage_type === 'WON') existingEffectiveProbability = 1.0;
                else if (currentStage.stage_type === 'LOST') existingEffectiveProbability = 0.0;
                else existingEffectiveProbability = currentStage.deal_probability;
            } else {
                console.warn(`[dealProbability.calculate] Could not fetch current stage ${oldDealData.stage_id} to recalculate weighted_amount.`);
            }
        }
    }

    if (typeof dealUpdateInput.amount === 'number' && existingEffectiveProbability !== null) {
        result.weighted_amount_to_set = dealUpdateInput.amount * existingEffectiveProbability;
        console.log('[dealProbability.calculate] Amount changed, recalculated weighted_amount:', result.weighted_amount_to_set);
    } else {
        result.weighted_amount_to_set = null;
        console.log('[dealProbability.calculate] Amount changed, but weighted_amount set to null due to missing effective probability or new amount.');
    }
  }
  return result;
};

