import type { GraphQLContext } from '../helpers';
import { requireAuthentication, getAccessToken } from '../helpers';
import { GraphQLError } from 'graphql';
import type { OrganizationResolvers, CustomFieldDefinition, CustomFieldValue } from '../../../../lib/generated/graphql';
import { CustomFieldEntityType, CustomFieldType } from '../../../../lib/generated/graphql';
import * as customFieldDefinitionService from '../../../../lib/customFieldDefinitionService';
import { getAuthenticatedClient } from '../../../../lib/serviceUtils';

export const Organization: OrganizationResolvers<GraphQLContext> = {
  people: async (_parent: { id: string }, _args: unknown, context: GraphQLContext) => {
     requireAuthentication(context);
     const accessToken = getAccessToken(context);
     if (!accessToken) throw new GraphQLError('Missing access token', { extensions: { code: 'UNAUTHENTICATED' } });
     console.warn(`Resolver Organization.people not fully implemented - needs service update`);
     return []; // Return empty array for now
  },

  customFieldValues: async (parent: { id: string; db_custom_field_values?: Record<string, any> | null }, _args: unknown, context: GraphQLContext): Promise<CustomFieldValue[]> => {
    requireAuthentication(context);
    const accessToken = getAccessToken(context);
    if (!accessToken) {
      console.error('Organization.customFieldValues: Missing access token');
      throw new GraphQLError('Missing access token', { extensions: { code: 'UNAUTHENTICATED' } });
    }
    const supabase = getAuthenticatedClient(accessToken);
    const organizationIdForLog = parent.id || 'unknown_organization_id';
    console.log(`[Organization.customFieldValues] Resolver START for organization: ${organizationIdForLog}`);

    const organizationSpecificValues = parent.db_custom_field_values || {};
    console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, received parent.db_custom_field_values:`, JSON.stringify(organizationSpecificValues));

    try {
      console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, fetching definitions for Organization entity.`);
      const definitions: CustomFieldDefinition[] = await customFieldDefinitionService.getCustomFieldDefinitions(
        supabase,
        CustomFieldEntityType.Organization, // Use enum value
        false // includeInactive = false
      );
      console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, loaded ${definitions.length} active Organization definitions:`, JSON.stringify(definitions.map(d => d.fieldName)));

      if (!definitions || definitions.length === 0) {
        console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, no active Organization definitions found. Returning [].`);
        return [];
      }

      const mappedValues: CustomFieldValue[] = definitions
        .map((definition: CustomFieldDefinition) => {
          const rawValue = organizationSpecificValues[definition.fieldName];
          console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, Def: ${definition.fieldName} (${definition.fieldType}), RawValue from DB:`, rawValue);

          const fieldValue: CustomFieldValue = {
            definition: definition, 
            stringValue: null,
            numberValue: null,
            booleanValue: null,
            dateValue: null,
            selectedOptionValues: null,
          };

          if (rawValue === undefined || rawValue === null) {
            console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, Def: ${definition.fieldName}, rawValue is null/undefined. FieldValue will have nulls.`);
            return fieldValue; 
          }

          switch (definition.fieldType) {
            case CustomFieldType.Text: 
              fieldValue.stringValue = String(rawValue);
              break;
            case CustomFieldType.Number:
              const num = parseFloat(String(rawValue));
              if (!isNaN(num)) {
                fieldValue.numberValue = num;
              } else {
                console.warn(`[Organization.customFieldValues] organization: ${organizationIdForLog}, Def: ${definition.fieldName}, could not parse number:`, rawValue);
              }
              break;
            case CustomFieldType.Boolean:
              fieldValue.booleanValue = Boolean(rawValue);
              break;
            case CustomFieldType.Date:
              fieldValue.dateValue = rawValue; 
              break;
            case CustomFieldType.Dropdown: 
              if (Array.isArray(rawValue) && rawValue.length > 0) {
                fieldValue.selectedOptionValues = [String(rawValue[0])]; 
              } else if (typeof rawValue === 'string') { 
                fieldValue.selectedOptionValues = [rawValue];
              }
              break;
            case CustomFieldType.MultiSelect:
              if (Array.isArray(rawValue)) {
                fieldValue.selectedOptionValues = rawValue.map(String);
              }
              break;
            default:
              console.warn(`[Organization.customFieldValues] organization: ${organizationIdForLog}, Def: ${definition.fieldName}, Unhandled custom field type: ${definition.fieldType as string}`);
          }
          console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, Def: ${definition.fieldName}, mapped fieldValue:`, JSON.stringify(fieldValue));
          return fieldValue;
        });

      console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, mappedValues before filter (${mappedValues.length} items):`, JSON.stringify(mappedValues.map(fv => ({ def: fv.definition.fieldName, sv: fv.stringValue, nv: fv.numberValue, bv: fv.booleanValue, dv: fv.dateValue, sov: fv.selectedOptionValues }))));

      const resolvedValues = mappedValues.filter(fv => 
          fv.stringValue !== null || 
          fv.numberValue !== null || 
          fv.booleanValue !== null || 
          fv.dateValue !== null || 
          (fv.selectedOptionValues && fv.selectedOptionValues.length > 0)
      );

      console.log(`[Organization.customFieldValues] organization: ${organizationIdForLog}, resolvedValues after filter (${resolvedValues.length} items):`, JSON.stringify(resolvedValues.map(fv => ({ def: fv.definition.fieldName, val: fv.stringValue || fv.numberValue || fv.booleanValue || fv.dateValue || fv.selectedOptionValues }))));
      console.log(`[Organization.customFieldValues] Resolver END for organization: ${organizationIdForLog}`);
      return resolvedValues;

    } catch (error) {
      console.error(`Error resolving customFieldValues for organization ${organizationIdForLog}:`, error);
      if (error instanceof GraphQLError) throw error;
      throw new GraphQLError('Could not resolve custom field values for the organization.', {
        extensions: { code: 'INTERNAL_SERVER_ERROR' },
      });
    }
  }
};

// Resolvers for Person type fields
import { GraphQLError } from 'graphql';
import { GraphQLContext, requireAuthentication, getAccessToken } from '../helpers.js';
import { organizationService } from '../../../../lib/organizationService.js';
// Import types with .js extension as required by NodeNext for actual .js files at runtime
import type { PersonResolvers, Organization, CustomFieldValue as GraphQLCustomFieldValue, CustomFieldDefinition as GraphQLCustomFieldDefinition, Person as GraphQLPerson, Deal as GraphQLDeal, Maybe } from '../../../../lib/generated/graphql.js'; 
import { CustomFieldEntityType, CustomFieldType } from '../../../../lib/generated/graphql.js'; // Also with .js
import * as customFieldDefinitionService from '../../../../lib/customFieldDefinitionService.js';
import { getAuthenticatedClient } from '../../../../lib/serviceUtils.js';
import { dealService } from '../../../../lib/dealService.js'; // Keep commented until deals resolver is implemented

// Interface for the parent object IF it has db_custom_field_values.
// The actual parent for PersonResolvers is GraphQLPerson.
interface PersonWithRawCustomFields extends GraphQLPerson { // Renamed for clarity
  db_custom_field_values?: Record<string, unknown> | null; 
}

// Define parent types for context within these resolvers - Will be removed as types are inferred
// type PersonOrganizationParent = { 
//   organization_id?: string | null 
// };

// type PersonDealsParent = { 
//   id: string 
// };

export const Person: PersonResolvers<GraphQLContext> = { // ParentType defaults to GraphQLPerson
    // Resolver for the nested 'organization' field within Person
    organization: async (parent: GraphQLPerson, _args: Record<string, never>, context: GraphQLContext): Promise<Maybe<Organization>> => {
      requireAuthentication(context);
      const accessToken = getAccessToken(context);
      if (!accessToken) throw new GraphQLError('Missing access token', { extensions: { code: 'UNAUTHENTICATED' } });

      if (!parent.organization_id) {
        return null;
      }
      try {
        const orgRecord: Organization | null = await organizationService.getOrganizationById(context.currentUser!.id, parent.organization_id, accessToken) as (Organization | null);
        if (!orgRecord) {
          return null;
        }
        // orgRecord is already a GraphQL Organization object, which includes fields like activities, deals, etc.
        // These will be resolved by their respective resolvers on the Organization type.
        return orgRecord; 
      } catch (e) {
        console.error('Error fetching Person.organization:', e);
        return null; 
      }
    },
    // Placeholder for deals linked to a person (requires dealService update)
    deals: async (parent: GraphQLPerson, _args: Record<string, never>, context: GraphQLContext): Promise<Maybe<Array<GraphQLDeal>>> => {
      requireAuthentication(context);
      const accessToken = getAccessToken(context);
      if (!accessToken) throw new GraphQLError('Missing access token', { extensions: { code: 'UNAUTHENTICATED' } });

      if (!parent.id) {
        console.error('[Person.deals] Person ID is missing from parent object.');
        return []; // Or throw error, depending on desired behavior for missing parent ID
      }

      try {
        console.log(`[Person.deals] Fetching deals for person ID: ${parent.id}`);
        const deals = await dealService.getDealsByPersonId(parent.id, accessToken);
        console.log(`[Person.deals] Found ${deals.length} deals for person ID: ${parent.id}`);
        return deals;
      } catch (e: any) {
        console.error(`Error fetching Person.deals for person ID ${parent.id}:`, e);
        // Optionally, re-throw specific errors or a generic one
        if (e instanceof GraphQLError) throw e;
        throw new GraphQLError('Could not fetch deals for the person.', {
          extensions: { code: 'INTERNAL_SERVER_ERROR', originalError: e.message },
        });
      }
    },
    customFieldValues: async (parentAsGraphQLPerson: GraphQLPerson, _args: Record<string, never>, context: GraphQLContext): Promise<GraphQLCustomFieldValue[]> => {
      // Type assertion for parent to access db_custom_field_values
      const parent = parentAsGraphQLPerson as PersonWithRawCustomFields;

      requireAuthentication(context);
      const accessToken = getAccessToken(context)!;
      const supabase = getAuthenticatedClient(accessToken);

      const personIdForLog = parent.id || 'unknown_person_id';
      console.log(`[Person.customFieldValues] Resolver START for person: ${personIdForLog}`);

      const personSpecificValues = parent.db_custom_field_values || {};
      console.log(`[Person.customFieldValues] person: ${personIdForLog}, received parent.db_custom_field_values:`, JSON.stringify(personSpecificValues));

      if (Object.keys(personSpecificValues).length === 0) {
        console.log(`[Person.customFieldValues] person: ${personIdForLog}, no custom field values found in parent data. Returning [].`);
        return [];
      }

      try {
        console.log(`[Person.customFieldValues] person: ${personIdForLog}, fetching definitions for Person entity.`);

        const definitions: Array<GraphQLCustomFieldDefinition> = await customFieldDefinitionService.getCustomFieldDefinitions(
          supabase,
          CustomFieldEntityType.Person,
          false 
        ) as GraphQLCustomFieldDefinition[];
        console.log(`[Person.customFieldValues] person: ${personIdForLog}, loaded ${definitions.length} active Person definitions:`, JSON.stringify(definitions.map(d => d.fieldName)));

        if (!definitions || definitions.length === 0) {
          console.log(`[Person.customFieldValues] person: ${personIdForLog}, no active Person definitions found. Returning [].`);
          return [];
        }

        const mappedValues: GraphQLCustomFieldValue[] = definitions
          .map((definition: GraphQLCustomFieldDefinition) => {
            let rawValue: unknown;
            if (definition.fieldName in personSpecificValues) {
              rawValue = personSpecificValues[definition.fieldName];
            } else {
              console.log(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName}, fieldName not found in personSpecificValues.`);
            }

            console.log(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName} (${definition.fieldType}), RawValue from DB:`, (rawValue !== null && typeof rawValue === 'object') ? JSON.stringify(rawValue) : rawValue);

            const fieldValue: GraphQLCustomFieldValue = {
              definition: definition, // Send full definition
              stringValue: null,
              numberValue: null,
              booleanValue: null,
              dateValue: null,
              selectedOptionValues: null,
            };

            if (rawValue === undefined || rawValue === null) {
              // Retain the definition, but all values will be null
              console.log(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName}, rawValue is null/undefined. FieldValue will have nulls.`);
              return fieldValue; 
            }

            switch (definition.fieldType) {
              case CustomFieldType.Text: {
                fieldValue.stringValue = String(rawValue);
                break;
              }
              case CustomFieldType.Number: {
                const num = parseFloat(String(rawValue)); // Ensure string for parseFloat
                if (!isNaN(num)) {
                  fieldValue.numberValue = num;
                } else {
                  console.warn(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName}, could not parse number:`, (rawValue !== null && typeof rawValue === 'object') ? JSON.stringify(rawValue) : rawValue);
                }
                break;
              }
              case CustomFieldType.Boolean: {
                fieldValue.booleanValue = Boolean(rawValue);
                break;
              }
              case CustomFieldType.Date: {
                if (rawValue && (typeof rawValue === 'string' || rawValue instanceof Date)) {
                  const date = new Date(rawValue);
                  if (!isNaN(date.getTime())) {
                    fieldValue.dateValue = date;
                  } else {
                    console.warn(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName}, could not parse date:`, (rawValue !== null && typeof rawValue === 'object') ? JSON.stringify(rawValue) : rawValue);
                  }
                } else if (rawValue !== null && rawValue !== undefined) { 
                  console.warn(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName}, received invalid type for date:`, (rawValue !== null && typeof rawValue === 'object') ? JSON.stringify(rawValue) : rawValue);
                }
                break;
              }
              case CustomFieldType.Dropdown: {
                if (Array.isArray(rawValue) && rawValue.length > 0) {
                  fieldValue.selectedOptionValues = [String(rawValue[0])];
                } else if (typeof rawValue === 'string') { 
                  fieldValue.selectedOptionValues = [rawValue];
                }
                break;
              }
              case CustomFieldType.MultiSelect: {
                if (Array.isArray(rawValue)) {
                  fieldValue.selectedOptionValues = rawValue.map(String);
                }
                break;
              }
              default: {
                console.warn(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName}, Unhandled custom field type: ${definition.fieldType}`);
              }
            }
            console.log(`[Person.customFieldValues] person: ${personIdForLog}, Def: ${definition.fieldName}, mapped fieldValue:`, JSON.stringify(fieldValue));
            return fieldValue;
          });

        console.log(`[Person.customFieldValues] person: ${personIdForLog}, mappedValues before filter (${mappedValues.length} items):`, JSON.stringify(mappedValues.map(fv => ({ def: fv.definition.fieldName, sv: fv.stringValue, nv: fv.numberValue, bv: fv.booleanValue, dv: fv.dateValue, sov: fv.selectedOptionValues }))));

        const resolvedValues = mappedValues.filter(fv => 
            fv.stringValue !== null || 
            fv.numberValue !== null || 
            fv.booleanValue !== null || 
            fv.dateValue !== null || 
            (fv.selectedOptionValues && fv.selectedOptionValues.length > 0)
        );

        console.log(`[Person.customFieldValues] person: ${personIdForLog}, resolvedValues after filter (${resolvedValues.length} items):`, JSON.stringify(resolvedValues.map(fv => ({ def: fv.definition.fieldName, val: fv.stringValue || fv.numberValue || fv.booleanValue || fv.dateValue || fv.selectedOptionValues }))));
        console.log(`[Person.customFieldValues] Resolver END for person: ${personIdForLog}`);
        return resolvedValues;

      } catch (error) {
        console.error('Error resolving customFieldValues for person:', parent.id, error);
        if (error instanceof GraphQLError) throw error;
        throw new GraphQLError('Could not resolve custom field values for the person.', {
          extensions: { code: 'INTERNAL_SERVER_ERROR' },
        });
      }
    }
};

import { priceQuoteService } from '../../../../lib/priceQuoteService';
// import { priceCalculator } from '../../../../lib/priceCalculator'; // Not directly used if service handles calculations
import type { GraphQLContext } from '../helpers';
import type { PriceQuote as PriceQuoteType, AdditionalCost, InvoiceScheduleEntry } from '../../../../lib/pricingTypes'; // Using our TS types
import { dealService } from '../../../../lib/dealService'; 
import { getServiceLevelUserProfileData } from '../../../../lib/userProfileService';
import type { Deal } from '../../../../lib/generated/graphql'; // Assuming Deal type is available
import type { User } from '../../../../lib/generated/graphql'; // Assuming User type is available or define a suitable one
// TODO: Import DealService and UserService/UserProfileService types/functions when available for relational resolvers
// Example: import { dealService } from '../../../../lib/dealService'; 
// Example: import { userProfileService } from '../../../../lib/userProfileService';

export const Query = {
  priceQuote: async (_parent: any, args: { id: string }, context: GraphQLContext): Promise<PriceQuoteType | null> => {
    if (!context.currentUser || !context.token) throw new Error("Unauthorized");
    try {
      return await priceQuoteService.getPriceQuoteById(args.id, context.currentUser.id, context.token);
    } catch (error: any) { 
      console.error(`Error fetching priceQuote by ID ${args.id}:`, error);
      throw new Error(`Failed to fetch price quote: ${error.message}`);
    }
  },
  priceQuotesForDeal: async (_parent: any, args: { dealId: string }, context: GraphQLContext): Promise<PriceQuoteType[]> => {
    if (!context.currentUser || !context.token) throw new Error("Unauthorized");
    try {
      return await priceQuoteService.listPriceQuotesForDeal(args.dealId, context.currentUser.id, context.token);
    } catch (error: any) {
      console.error(`Error fetching priceQuotesForDeal ${args.dealId}:`, error);
      throw new Error(`Failed to fetch price quotes for deal: ${error.message}`);
    }
  },
};

// Field resolvers for the PriceQuote type
export const PriceQuoteResolver = { 
  deal: async (parent: PriceQuoteType, _args: any, context: GraphQLContext): Promise<Deal | null> => {
    if (!context.currentUser || !context.token) throw new Error("Unauthorized");
    if (!parent.deal_id) return null;
    try {
      // Assuming dealService.getDealById expects userId first, then dealId, then token
      return await dealService.getDealById(context.currentUser.id, parent.deal_id, context.token);
    } catch (error: any) {
      console.error(`Error resolving deal ${parent.deal_id} for quote ${parent.id}:`, error.message);
      // Decide if to throw, or return null to allow partial responses
      return null; 
    }
  },
  user: async (parent: PriceQuoteType, _args: any, context: GraphQLContext): Promise<User | null> => {
    // Note: context.currentUser might already have some user info. 
    // However, parent.user_id refers to the quote owner, which might not always be the currentUser making the request (e.g. admin access)
    if (!parent.user_id) return null;
    // getServiceLevelUserProfileData does not require a token as it uses the service client
    // Ensure it's safe to call without further auth checks if context.currentUser might differ from parent.user_id
    // For now, assume direct call is okay if parent.user_id is present.
    try {
      const userProfile = await getServiceLevelUserProfileData(parent.user_id);
      if (!userProfile) return null;

      // Adapt userProfile to the GraphQL User type
      // The User type in generated/graphql might have id, email, name, avatarUrl fields.
      // ServiceLevelUserProfile has user_id, email, display_name, avatar_url.
      return {
        id: userProfile.user_id, // Map user_id to id
        email: userProfile.email,
        name: userProfile.display_name, // Map display_name to name
        avatarUrl: userProfile.avatar_url, // Map avatar_url to avatarUrl
        // Add any other fields required by the GraphQL User type, possibly with null/default values
      } as User; // Cast to User, ensure all required fields of User are populated
    } catch (error: any) {
      console.error(`Error resolving user ${parent.user_id} for quote ${parent.id}:`, error.message);
      return null;
    }
  },

  // additional_costs and invoice_schedule_entries are assumed to be populated by the service methods 
  // (e.g., getPriceQuoteById, listPriceQuotesForDeal) directly onto the parent PriceQuote object.
  // If they were separate service calls, resolvers would look like this:
  /*
  additional_costs: async (parent: PriceQuoteType, _args: any, context: GraphQLContext): Promise<AdditionalCost[]> => {
    if (!context.currentUser || !context.token) throw new Error("Unauthorized");
    // Example: return await priceQuoteService.getAdditionalCostsForQuote(parent.id, context.currentUser.id, context.token);
    // For now, assume parent.additional_costs is populated by the primary service call that resolved the PriceQuote.
    if (parent.additional_costs) return parent.additional_costs;
    throw new Error ('Additional costs resolver not fully implemented or data not pre-fetched.');
  },
  invoice_schedule_entries: async (parent: PriceQuoteType, _args: any, context: GraphQLContext): Promise<InvoiceScheduleEntry[]> => {
    if (!context.currentUser || !context.token) throw new Error("Unauthorized");
    // Example: return await priceQuoteService.getInvoiceScheduleEntriesForQuote(parent.id, context.currentUser.id, context.token);
    if (parent.invoice_schedule_entries) return parent.invoice_schedule_entries;
    throw new Error ('Invoice schedule entries resolver not fully implemented or data not pre-fetched.');
  },
  */

  // Calculated fields are expected to be on the parent object, either from DB or calculated by priceQuoteService.
  // If a calculated field needed specific logic only possible at GraphQL resolver time (rare), it would go here.
  // Example: 
  // calculated_total_direct_cost: (parent: PriceQuoteType) => { 
  //   if (parent.calculated_total_direct_cost != null) return parent.calculated_total_direct_cost;
  //   // Fallback calculation if needed, though service should handle this.
  //   const mp = parent.base_minimum_price_mp || 0;
  //   const acItems = parent.additional_costs?.map(ac => ({ amount: ac.amount })) || [];
