export const Query: QueryResolvers<GraphQLContext> = {
    health: () => 'OK',
    supabaseConnectionTest: async () => {
      try {
        const { data, error } = await supabase.auth.getSession();
        if (error) {
          console.error('Supabase connection error:', error.message);
          return `Connection Error: ${error.message}`;
        }
        console.log('Supabase getSession data:', data);
        return 'Successfully connected to Supabase (getSession succeeded)';
      } catch (error: unknown) {
        console.error('Unexpected error during Supabase test:', error);
        let message = "Unknown error during Supabase test";
        if (error instanceof Error) {
            message = error.message;
        } else if (typeof error === 'string') {
            message = error;
        }
        return `Unexpected Error: ${message}`;
      }
    },
    me: async (_parent, _args, context: GraphQLContext): Promise<GraphQLUser | null> => {
      requireAuthentication(context);
      const currentUser = context.currentUser!;
      const accessToken = getAccessToken(context)!;

      if (!currentUser.email) {
        // This case should ideally not happen for an authenticated user if email is a primary identifier.
        // Log an error and throw, as the GraphQL schema expects a non-null email for the User type.
        console.error(`[Query.me] Critical: Authenticated user ${currentUser.id} has no email.`);
        throw new GraphQLError('Authenticated user email is missing.', { extensions: { code: 'INTERNAL_SERVER_ERROR' } });
      }

      try {
        const profile = await userProfileService.getUserProfile(currentUser.id, accessToken);

        return {
          id: currentUser.id,
          email: currentUser.email, // Now checked for null/undefined
          display_name: profile?.display_name || null,
          avatar_url: profile?.avatar_url || null,    
        };
      } catch (error) {
        console.error(`[Query.me] Error fetching profile for user ${currentUser.id}:`, error);
        // Fallback: return basic info, but email is guaranteed by the check above.
        return {
            id: currentUser.id,
            email: currentUser.email, // currentUser.email is guaranteed here
            display_name: null,
            avatar_url: null,
        }; 
      }
    },

    // --- Person Resolvers ---\
    people: async (_parent, _args, context) => {
      const action = 'fetching people list';
      try {
          requireAuthentication(context);
          const accessToken = getAccessToken(context)!;
          const peopleList = await personService.getPeople(context.currentUser!.id, accessToken);
          // Map PersonRecord[] to GraphQLPerson[]
          return peopleList.map(p => ({
            id: p.id,
            created_at: p.created_at,
            updated_at: p.updated_at,
            user_id: p.user_id,
            first_name: p.first_name,
            last_name: p.last_name,
            email: p.email,
            phone: p.phone,
            notes: p.notes,
            organization_id: p.organization_id,
            // Make raw DB data available for the Person.customFieldValues resolver
            db_custom_field_values: (p).custom_field_values,
            // organization, deals, activities resolved by Person type resolvers
          })) as any; // Cast to any to allow db_custom_field_values
      } catch (e) {
         throw processZodError(e, action);
      }
    },
    person: async (_parent, args, context) => {
      const action = 'fetching person by ID';
      try {
          requireAuthentication(context);
          const accessToken = getAccessToken(context)!;
          const p = await personService.getPersonById(context.currentUser!.id, args.id, accessToken);
          if (!p) return null;
          // Map PersonRecord to GraphQLPerson
          return {
            id: p.id,
            created_at: p.created_at,
            updated_at: p.updated_at,
            user_id: p.user_id,
            first_name: p.first_name,
            last_name: p.last_name,
            email: p.email,
            phone: p.phone,
            notes: p.notes,
            organization_id: p.organization_id,
            // Make raw DB data available for the Person.customFieldValues resolver
            db_custom_field_values: (p as any).custom_field_values,
          } as any; // Cast to any to allow db_custom_field_values
      } catch (e) {
         throw processZodError(e, action);
      }
    },
    personList: async (_parent, _args, context) => {
      requireAuthentication(context);
      const accessToken = getAccessToken(context)!;
      try {
        const people = await personService.getPeople(context.currentUser!.id, accessToken); 
        return people.map(p => ({
            id: p.id,
            name: [p.first_name, p.last_name].filter(Boolean).join(' ') || p.email || 'Unnamed Person'
        })) as GraphQLPersonListItem[];
      } catch (e) {
         throw processZodError(e, 'fetching person list for dropdown');
      }
    },

    // --- Organization Resolvers ---\
    organizations: async (_parent, _args, context) => {
       requireAuthentication(context);
       const accessToken = getAccessToken(context)!;
       try {
         const orgs = await organizationService.getOrganizations(context.currentUser!.id, accessToken);
         // Map OrganizationRecord[] to GraphQLOrganization[]
         return orgs.map(o => ({
            id: o.id,
            created_at: o.created_at,
            updated_at: o.updated_at,
            user_id: o.user_id,
            name: o.name,
            address: o.address,
            notes: o.notes,
            db_custom_field_values: (o).custom_field_values,
            // activities, deals, people resolved by Organization type resolvers
         })) as any; // Cast to any to allow db_custom_field_values
       } catch (e) {
          throw processZodError(e, 'fetching organizations list');
       }
    },
    organization: async (_parent, args, context) => {
       requireAuthentication(context);
       const accessToken = getAccessToken(context)!;
       try {
          const o = await organizationService.getOrganizationById(context.currentUser!.id, args.id, accessToken);
          if (!o) return null;
          // Map OrganizationRecord to GraphQLOrganization
          return {
            id: o.id,
            created_at: o.created_at,
            updated_at: o.updated_at,
            user_id: o.user_id,
            name: o.name,
            address: o.address,
            notes: o.notes,
            db_custom_field_values: (o as any).custom_field_values,
          } as any; // Cast to any to allow db_custom_field_values
       } catch (e) {
           throw processZodError(e, 'fetching organization by ID');
       }
    },

    // --- Deal Resolvers ---\
    deals: async (_parent, _args, context) => {
       requireAuthentication(context);
       const accessToken = getAccessToken(context)!;
       try {
           const dealList = await dealService.getDeals(context.currentUser!.id, accessToken);
           return dealList.map(d => ({
                id: d.id,
                user_id: d.user_id!,
                created_at: d.created_at,
                updated_at: d.updated_at,
                name: d.name!, 
                amount: d.amount,
                expected_close_date: d.expected_close_date,
                pipeline_id: d.pipeline_id!,
                stage_id: d.stage_id!, 
                person_id: d.person_id,
                organization_id: d.organization_id,
                deal_specific_probability: d.deal_specific_probability,
                // Make raw DB data available for the Deal.customFieldValues resolver
                db_custom_field_values: (d).custom_field_values, 
           })) as any; // Cast to any to allow db_custom_field_values, field resolvers will complete the type
       } catch (e) {
           throw processZodError(e, 'fetching deals list');
       }
    },
    deal: async (_parent, args, context) => {
       requireAuthentication(context);
       const accessToken = getAccessToken(context)!;
       try {
           const d = await dealService.getDealById(context.currentUser!.id, args.id, accessToken);
           if (!d) return null;
           return {
                id: d.id,
                user_id: d.user_id!,
                created_at: d.created_at,
                updated_at: d.updated_at,
                name: d.name!, 
                amount: d.amount,
                expected_close_date: d.expected_close_date,
                pipeline_id: d.pipeline_id!,
                stage_id: d.stage_id!, 
                person_id: d.person_id,
                organization_id: d.organization_id,
                deal_specific_probability: d.deal_specific_probability,
                // Make raw DB data available for the Deal.customFieldValues resolver
                db_custom_field_values: (d as any).custom_field_values, 
           } as any; // Cast to any, field resolvers will complete the type
       } catch (e) {
           throw processZodError(e, 'fetching deal by ID');
       }
    },
    // Pipeline Resolvers
    pipelines: async (_parent, _args, context: GraphQLContext): Promise<GraphQLPipeline[]> => {
      requireAuthentication(context);
      const accessToken = getAccessToken(context)!;
      try {
        const pipelineList = await pipelineService.getPipelines(accessToken);
        return pipelineList.map(p => ({
          id: p.id,
          user_id: p.user_id,
          name: p.name,
          created_at: p.created_at, 
          updated_at: p.updated_at,
        })) as GraphQLPipeline[];
      } catch (e) {
        throw processZodError(e, 'fetching pipelines list');
      }
    },
    // Ensure there isn't a singular 'pipeline(id: ID!)' resolver here unless also defined in schema

    // Stage Resolvers
    stages: async (_parent, args, context) => {
        const action = 'fetching stages for pipeline ' + args.pipelineId;
        try {
            requireAuthentication(context);
            const accessToken = getAccessToken(context)!;
            if (!args.pipelineId) throw new GraphQLError("pipelineId is required", { extensions: { code: 'BAD_USER_INPUT' } });
            const stageList = await stageService.getStagesByPipelineId(accessToken, args.pipelineId);
            // Map Stage from lib/types to GraphQLStage
            return stageList.map(s => ({
                id: s.id,
                user_id: s.user_id,
                pipeline_id: s.pipeline_id,
                name: s.name,
                order: s.order,
                deal_probability: s.deal_probability,
                stage_type: s.stage_type,
                created_at: s.created_at,
                updated_at: s.updated_at,
                // pipeline field resolved by Stage type resolver
            })) as GraphQLStage[];
        } catch (error) { 
            console.error('[Query.stages] Error during ' + action + ':', error);
            throw processZodError(error, action); 
        }
    },
    stage: async (_parent, args, context) => {
        const action = 'fetching stage ' + args.id;
        try {
            requireAuthentication(context);
            const accessToken = getAccessToken(context)!;
            const s = await stageService.getStageById(accessToken, args.id);
            if (!s) return null;
            // Map Stage from lib/types to GraphQLStage
             return {
                id: s.id,
                user_id: s.user_id,
                pipeline_id: s.pipeline_id,
                name: s.name,
                order: s.order,
                deal_probability: s.deal_probability,
                stage_type: s.stage_type,
                created_at: s.created_at,
                updated_at: s.updated_at,
            } as GraphQLStage;
        } catch (error) {
            console.error('[Query.stage] Error during ' + action + ':', error);
            throw processZodError(error, action);
        }
    },
    // --- My Permissions Query ---\
    myPermissions: (_parent, _args, context) => {
        requireAuthentication(context);
        return context.userPermissions ?? []; // Already returns string[]
    },
};

import type { GraphQLContext } from '../helpers';
import { requireAuthentication, processZodError, getAccessToken } from '../helpers';
import { GraphQLError } from 'graphql';
import * as pipelineService from '../../../../lib/pipelineService';
import type { StageResolvers } from '../../../../lib/generated/graphql';

export const Stage: StageResolvers<GraphQLContext> = {
  pipeline: async (parent, _args, context) => {
    requireAuthentication(context);
    const accessToken = getAccessToken(context)!;

    try {
      const pipeline = await pipelineService.getPipelineById(accessToken, parent.pipeline_id);
      if (!pipeline) {
         console.error(`Pipeline ${parent.pipeline_id} not found for stage.`);
         throw new GraphQLError(`Pipeline associated with this stage not found.`, { extensions: { code: 'NOT_FOUND' } });
      }
      return pipeline;
    } catch (e) {
      console.error(`Error fetching pipeline ${parent.pipeline_id} for stage:`, e);
      throw processZodError(e, `fetching pipeline ${parent.pipeline_id}`);
    }
  }
};

import { z } from 'zod';
import { StageType as GeneratedStageType } from '../../../lib/generated/graphql'; // Adjusted path

// Zod schema for CustomFieldValueInput
const CustomFieldValueInputSchema = z.object({
  definitionId: z.string().uuid("Invalid Definition ID format for custom field value"),
  stringValue: z.string().optional().nullable(),
  numberValue: z.number().optional().nullable(),
  booleanValue: z.boolean().optional().nullable(),
  dateValue: z.coerce.date({ errorMap: () => ({ message: "Invalid date for custom field. Expected a valid date string." }) }).optional().nullable(), 
  selectedOptionValues: z.array(z.string()).optional().nullable(),
}).refine(data => {
  const valueFields = [
    data.stringValue,
    data.numberValue,
    data.booleanValue,
    data.dateValue,
    data.selectedOptionValues
  ];
  const providedValueFields = valueFields.filter(v => v !== undefined && v !== null && (!Array.isArray(v) || v.length > 0));
  // Ensure at most one value field is provided (0 is okay for optional, 1 is for a set value)
  return providedValueFields.length <= 1;
}, {
  message: "If providing a custom field value, only one of stringValue, numberValue, booleanValue, dateValue, or selectedOptionValues should be set.",
  // path: [], // General error, or pick one field e.g., 'stringValue'
});

// --- Person Schemas ---
const PersonBaseSchema = z.object({
  first_name: z.string().trim().min(1, { message: "First name cannot be empty if provided" }).optional().nullable(),
  last_name: z.string().trim().min(1, { message: "Last name cannot be empty if provided" }).optional().nullable(),
  email: z.string().trim().email({ message: "Invalid email address" }).optional().nullable(),
  phone: z.string().trim().optional().nullable(),
  notes: z.string().trim().optional().nullable(),
  organization_id: z.string().uuid({ message: "Invalid Organization ID format" }).optional().nullable(),
  customFields: z.array(CustomFieldValueInputSchema).optional().nullable(),
});

export const PersonCreateSchema = PersonBaseSchema.refine(data => data.first_name || data.last_name || data.email, {
  message: "At least a first name, last name, or email is required",
  path: ["first_name"],
});

export const PersonUpdateSchema = PersonBaseSchema.partial();

// --- Organization Schemas ---
export const OrganizationInputSchema = z.object({
    name: z.string().trim().min(1, { message: "Organization name cannot be empty" }),
    address: z.string().trim().optional().nullable(),
    notes: z.string().trim().optional().nullable(),
    customFields: z.array(CustomFieldValueInputSchema).optional().nullable(),
});

// --- Deal Schemas ---

export const DealBaseSchema = z.object({
  name: z.string().min(1, { message: "Deal name is required" }).optional(),
  amount: z.number().positive("Amount must be positive").optional().nullable(),
  expected_close_date: z.string().datetime().optional().nullable(),
  stage_id: z.string().uuid("Valid Stage ID is required").optional(),
  pipeline_id: z.string().uuid("Valid Pipeline ID is required").optional(),
  person_id: z.string().uuid("Valid Person ID is required").optional().nullable(),
  organization_id: z.string().uuid("Valid Organization ID is required").optional().nullable(),
  deal_specific_probability: z.number().min(0).max(1).optional().nullable(),
  customFields: z.array(CustomFieldValueInputSchema).optional().nullable(), // Added customFields to Zod schema
});

export const DealCreateSchema = DealBaseSchema.merge(z.object({
  name: z.string().min(1, { message: "Deal name is required" }),
  stage_id: z.string().uuid("Valid Stage ID is required"),
  pipeline_id: z.string().uuid("Valid Pipeline ID is required"),
}));
export const DealUpdateSchema = DealBaseSchema.partial();

// --- Pipeline Schemas ---
export const PipelineInputSchema = z.object({
    name: z.string().trim().min(1, { message: "Pipeline name cannot be empty" }),
});

// Define StageType enum for Zod validation, using imported GraphQL enum members
const StageTypeZodEnum = z.enum([
    GeneratedStageType.Open,
    GeneratedStageType.Won,
    GeneratedStageType.Lost
], { errorMap: () => ({ message: 'Invalid stage type' }) });

// --- Stage Schemas ---
const StageBaseSchema = z.object({
    name: z.string().trim().min(1, { message: "Stage name cannot be empty" }),
    order: z.number().int().nonnegative({ message: "Order must be a non-negative integer"}),
    pipeline_id: z.string().uuid({ message: "Invalid Pipeline ID format" }),
    deal_probability: z.number().min(0).max(1, { message: "Probability must be between 0.0 and 1.0" }).optional().nullable(),
    stage_type: StageTypeZodEnum.optional(), // Use the new Zod enum
});

export const StageCreateSchema = StageBaseSchema;
export const StageUpdateSchema = StageBaseSchema.partial().omit({ pipeline_id: true });

